    await core.initialize(owner);

    gop = await GOPCreator.new();
    await gop.initialize(core.address, owner);

    instance = await SaleAuction.new();
    await instance.initialize(core.address, owner);

    hd = await HorseData.new();
    await hd.initialize();

    await core.setGOPCreator(gop.address, { from: owner });
    await core.setSaleAuctionAddress(instance.address, { from: owner });
    await core.setHorseDataAddr(hd.address, { from: owner });

