const Breeding = artifacts.require('Breeding')
const Core = artifacts.require('Core')
const GOPCreator = artifacts.require('GOPCreatorV2')
const PrivGOP = artifacts.require('PrivateGOPCreatorV2')
const HorseData = artifacts.require('HorseData')
const StudService = artifacts.require('StudServiceV4')
const BreedTypes = artifacts.require('BreedTypes')

contract('Breeding', acc => {
  let core, breeding, privGOP, gop, hd, breedTypes, studService
  let owner = acc[1]
  let poolAddress = acc[9]
  let amount = web3.utils.toWei('1', 'ether')
  let priceyAmount = web3.utils.toWei('25', 'ether')

  before(async() => {
    Core.autogas = true
  })

  beforeEach('setup instances', async() => {
    core = await Core.new()
    await core.initialize(owner)

    privGOP = await PrivGOP.new();

    gop = await GOPCreator.new(owner, privGOP.address, core.address, acc[3], {
      from: owner
    })

    hd = await HorseData.new()
    await hd.initialize()

    breedTypes = await BreedTypes.new()
    await breedTypes.initialize(owner)
    await breedTypes.populateMatrix({
      from: owner
    })

    studService = await StudService.new(core.address, breedTypes.address, acc[2], {
      from: owner
    })

    breeding = await Breeding.new()
    await breeding.initialize(owner, core.address, studService.address, poolAddress,
      breedTypes.address, breeding.address)

    await core.setBreedingAddr(breeding.address, {
      from: owner
    })
    await core.setGOPCreator(gop.address, {
      from: owner
    })
    await core.setHorseDataAddr(hd.address, {
      from: owner
    })

    await breedTypes.setBreedingAddress(breeding.address, {
      from: owner
    })

    await gop.openBatch(1, {
      from: owner
    });

    // Creating a genesis token since we can't mix with a genesis horse.
    await gop.createGOP(owner, 'male hash', {
        from: owner,
        value: amount
      }) // 0 Genesis token
  })

  it('should mix two horses', async() => {
    // Mint two tokens.
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(acc[2], 'male hash', {
        from: acc[2],
        value: amount
      }) // 2

    await studService.putInStud(2, amount, 1000, {
      from: acc[2]
    })

    // This should create another token.
    await breeding.mix(2, 1, 'offspring hash', {
        from: owner,
        value: amount
      }) // 3

    let firstOffspringStats = await breeding.getHorseOffspringStats(1)
    let secondOffspringStats = await breeding.getHorseOffspringStats(1)

    assert.equal(web3.utils.toBN(firstOffspringStats)
      .toNumber(), 1)
    assert.equal(web3.utils.toBN(secondOffspringStats)
      .toNumber(), 1)

    // Ensure the owner is the owner of the female horse
    let ownerOfToken = await core.ownerOf(3)
    assert.equal(ownerOfToken, owner)
  })

  it('should create a base value for the offspring', async() => {
      await gop.createGOP(owner, 'female hash', {
          from: owner,
          value: amount
        }) // 1
      await gop.createGOP(acc[2], 'male hash', {
          from: acc[2],
          value: amount
        }) // 2

      await studService.putInStud(2, amount, 1000, {
        from: acc[2]
      })

      await breeding.mix(2, 1, 'offspring hash', {
          from: owner,
          value: amount
        }) // 3

      let baseValue = await core.getBaseValue(3)
      assert.notEqual(web3.utils.toBN(baseValue)
        .toNumber(), 0)

      // The parents of the third horse are horses from the first Gen, they have high base value
      // so offspring's base value shoul dbe at least 50.
      assert.isAbove(web3.utils.toBN(baseValue)
        .toNumber(), 50)
    })
    //
  it('should return the correct genotype for a given horse', async() => {
      // At this point we're going to use two ZED 1 (0) horses.
      await gop.createGOP(owner, 'female hash', {
          from: owner,
          value: amount
        }) // 1
      await gop.createGOP(acc[2], 'male hash', {
          from: acc[2],
          value: amount
        }) // 2

      await studService.putInStud(2, amount, 1000, {
        from: acc[2]
      })

      await breeding.mix(2, 1, 'offspring hash', {
          from: owner,
          value: amount
        }) // 3

      let genotype = await core.getHorseData(3) // At this point parents had 1 and 1 as genotype.
      assert.equal(web3.utils.toBN(genotype[5])
        .toNumber(), 2)
    })
    //
    // Maybe same as above tests but with a more direct approach
  it('should revert when mixing two offsprings from the same parents', async() => {
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(acc[2], 'male hash', {
        from: acc[2],
        value: amount
      }) // 2

    await studService.putInStud(2, amount, 1000, {
      from: acc[2]
    })

    let maleOffspring = await createOffspring(2, 1, 'offspring hash', owner, amount, 'M')
    let femaleOffspring = await createOffspring(2, 1, 'offspring hash', owner, amount, 'F')

    await studService.putInStud(maleOffspring, amount, 1000, {
      from: owner
    })

    // Mixing the two horses from the same parents (5 and 6)
    try {
      await breeding.mix(maleOffspring, femaleOffspring, 'failed female hash', {
        from: owner,
        value: amount
      })
      assert.fail('Expected revert not received')
    } catch(err) {
      assert.equal(err.reason, 'BR horsesAreBrothers')
    }
  })

  it('should revert when mixing an offspring with a parent', async() => {
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(acc[2], 'male hash', {
        from: acc[2],
        value: amount
      }) // 2

    await studService.putInStud(2, amount, 1000, {
      from: acc[2]
    })

    let femaleOffspring = await createOffspring(2, 1, 'offspring hash', owner, amount, 'F')

    // Mix 2 with 'femaleOffspring' which is an offspring of 2.
    try {
      await breeding.mix(2, femaleOffspring, 'failed female hash', {
        from: owner,
        value: priceyAmount
      })
      assert.fail('Expected revert not received')
    } catch(err) {
      assert.equal(err.reason, 'BR horsesAreDirectlyRelated')
    }
  })

  it('should revert when mixing two horses with the same sex', async() => {
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(acc[2], 'male hash', {
        from: acc[2],
        value: amount
      }) // 2
    await gop.createGOP(acc[2], 'female hash', {
        from: acc[2],
        value: amount
      }) // 3
    await gop.createGOP(acc[2], 'male hash', {
        from: acc[2],
        value: amount
      }) // 4

    await studService.putInStud(2, amount, 1000, {
      from: acc[2]
    })

    // 1 and 3 are two females and not related, if the second parameter isn't a female horse it'll throw.
    try {
      await breeding.mix(1, 3, 'failed male hash', {
        from: acc[4]
      })
      assert.fail('Expected revert not received')
    } catch(err) {
      assert.equal(err.reason, 'BR expectedMaleHorseReceivedFemale')
    }
  })

  it("should revert when the second parameter isn't a female horse", async() => {
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(acc[2], 'male hash', {
        from: acc[2],
        value: amount
      }) // 2
    await gop.createGOP(acc[2], 'female hash', {
        from: acc[2],
        value: amount
      }) // 3
    await gop.createGOP(acc[2], 'male hash', {
        from: acc[2],
        value: amount
      }) // 4

    await studService.putInStud(2, amount, 1000, {
      from: acc[2]
    })

    try {
      await breeding.mix(2, 4, 'failed male hash', {
        from: acc[3]
      })
      assert.fail('Expected revert not received')
    } catch(err) {
      assert.equal(err.reason, 'BR expectedFemaleHorseReceivedMale')
    }
  })

  it('should revert when mixing with ancestors', async() => {
    // Ancestors have a limit until grandparents, that would be two ancestors lines.
    // We'll go for Paternal grandparents
    // We'll use new horses for this.

    /*
           G     -      G
        |F - M   -   F - M|
          ||     -    ||
        S -- S   -  S -- S

        Family tree... sort of, we want to reach S and mate one of S with a G
    */

    // * Grandparents
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(owner, 'male hash', {
        from: owner,
        value: amount
      }) // 2
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 3
    await gop.createGOP(owner, 'male hash', {
        from: owner,
        value: amount
      }) // 4

    await studService.putInStud(2, amount, 1, {
      from: owner
    })
    await studService.putInStud(4, amount, 1, {
      from: owner
    })

    // parents 8, 9
    let firstOffspringMale = await createOffspring(2, 1, 'offspring', owner, amount, 'M')

    await studService.putInStud(firstOffspringMale, amount, 1, {
      from: owner
    })

    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // firstOffspringMale + 1
      // parents firstOffspringMale, firstOffspringMale + 1 / grandparents 2, 1.
    let secondOffspringMale = await createOffspring(firstOffspringMale, firstOffspringMale +
      1, 'ofspring', owner, amount, 'M')
    await studService.putInStud(secondOffspringMale, amount, 1, {
      from: owner
    })

    await gop.createGOP(owner, 'male hash', {
        from: owner,
        value: amount
      }) // secondOffspringMale + 1
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // secondOffspringMale + 2
      // parents firstOffspringMale, secondOffspringMale - grandparents 2, 1.
    await createOffspring(firstOffspringMale, secondOffspringMale + 2, 'offspring', owner,
      amount, 'M')

    // Trying to mate 12 with 9 should revert.
    try {
      await breeding.mix(secondOffspringMale, 1, 'failed female hash', {
        from: owner,
        value: amount
      })
      assert.fail('Expected revert not received')
    } catch(err) {
      assert.equal(err.reason, 'BR horseIsGrandchild')
    }
  })

  it('should send part of the reserve amount to the owner of the horse when mixing',
    async() => {
      await gop.createGOP(owner, 'female hash', {
          from: owner,
          value: amount
        }) // 1
      await gop.createGOP(acc[3], 'male hash', {
          from: owner,
          value: amount
        }) // 2

      let preBalance = web3.utils.toWei(await web3.eth.getBalance(acc[3]))

      await studService.putInStud(2, amount, 1, {
        from: acc[3]
      })

      await breeding.mix(2, 1, 'male offspring hash', {
          from: owner,
          value: priceyAmount
        }) // 16

      let currBalance = web3.utils.toWei(await web3.eth.getBalance(acc[3]))

      assert.isTrue(preBalance < currBalance)
    })

  it('should select the correct bloodline based in parents', async() => {
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(acc[6], 'male hash', {
        from: acc[6],
        value: amount
      }) // 2
    await studService.putInStud(2, amount, 1, {
      from: acc[6]
    })
    await breeding.mix(2, 1, 'offspring hash', {
        from: owner,
        value: amount
      }) // 3

    let data = await core.getHorseData(3) // Nakamoto + Nakamoto = Nakamoto

    assert.equal(web3.utils.hexToUtf8(data[6]), 'N')

    for(let i = 3; i <= 300; i++) {
      if(i == 100) {
        await gop.openBatch(2, {
          from: owner
        })
      } else if(i == 299) {
        await gop.openBatch(3, {
          from: owner
        })
      }

      // We're going to create ~300 horses so we have a different genotype
      await gop.createGOP(acc[5], 'random hash', {
        from: acc[5],
        value: amount
      })
    }

    await breeding.mix(2, 300, 'male offspring hash', {
      from: acc[5],
      value: amount
    })

    let lastHorse = await core.totalSupply()

    data = await core.getHorseData(lastHorse - 1)
    assert.equal(web3.utils.hexToUtf8(data[6]), 'S')
  })

  it('should change the type of the horse once it has has its first offspring', async() => {
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(acc[6], 'male hash', {
        from: acc[6],
        value: amount
      }) // 2
    await studService.putInStud(2, amount, 1, {
      from: acc[6]
    })

    let maleType = await core.getHorseData(2)
    let femaleType = await core.getHorseData(1)

    assert.equal(web3.utils.hexToUtf8(maleType[7]), 'Colt') // Colt
    assert.equal(web3.utils.hexToUtf8(femaleType[7]), 'Filly') // Filly

    await breeding.mix(2, 1, 'offspring hash', {
      from: owner,
      value: amount
    })

    maleType = await core.getHorseData(2)
    femaleType = await core.getHorseData(1)

    assert.equal(web3.utils.hexToUtf8(maleType[7]), 'Stallion')
    assert.equal(web3.utils.hexToUtf8(femaleType[7]), 'Mare')
  })

  it('should distribute funds differently when owners are not the same', async() => {
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(acc[6], 'male hash', {
        from: acc[6],
        value: amount
      }) // 2
    await studService.putInStud(2, amount, 1, {
      from: acc[6]
    })

    // Public - not the same stable
    let breed = await breeding.mix(2, 1, 'male offspring hash', {
      from: owner,
      value: amount
    })
    let breedingFunds = breed.logs[0]

    // horse 18 is in stud for 1 day (default)
    // owner should get 24%
    let studOwnerPay = amount * 24 / 100

    // races pool should get 56%
    let racesPoolPay = amount * 56 / 100

    // zed should get 0%

    assert.equal(breedingFunds.args._studOwner.toString(), studOwnerPay.toString())
    assert.equal(breedingFunds.args._racesPool.toString(), racesPoolPay.toString())
    assert.equal(breedingFunds.args._zed.toString(), '0')
  })

  it('should distribute the funds differently when owners are the same', async() => {
    // Private - same owner
    await gop.createGOP(owner, 'female hash', {
        from: owner,
        value: amount
      }) // 1
    await gop.createGOP(owner, 'male hash', {
        from: owner,
        value: amount
      }) // 2
    await studService.putInStud(2, amount, 100, {
      from: owner
    })

    let breed = await breeding.mix(2, 1, 'offspring hash', {
      from: owner,
      value: amount
    })
    let breedingFunds = breed.logs[0]

    // stud owner should get 0
    // races pool should get 100%
    // zed should get 0%

    assert.equal(breedingFunds.args._studOwner.toString(), '0')
    assert.equal(breedingFunds.args._racesPool.toString(), amount.toString())
    assert.equal(breedingFunds.args._zed.toString(), '0')
  })

  let createOffspring = async(male, female, hash, from, value, wantedGender) => {
    let lastHorseId

    while(true) {
      await breeding.mix(male, female, hash, {
        from: from,
        value: value
      })
      let lastHorse = await core.totalSupply()
      let sex = await core.getHorseSex(lastHorse - 1)

      if(web3.utils.toUtf8(sex) === wantedGender) {
        lastHorseId = lastHorse - 1
        break
      }
    }

    return lastHorseId
  }
})

