const Core = artifacts.require("Core");
const GOPCreator = artifacts.require("GOPCreatorV2");
const PrivGOP = artifacts.require('PrivateGOPCreatorV2')
const Breeding = artifacts.require("Breeding");
const StudService = artifacts.require("StudServiceV4");
const BreedTypes = artifacts.require("BreedTypes");
const HorseData = artifacts.require("HorseData");

contract("BreedTypes", acc => {
  let core, breedTypes, breeding, studService, privGOP, gop, hd;
  let horseValue = web3.utils.toWei("1", "ether");
  let query = web3.utils.toWei("0.5", "ether");
  let owner = acc[1];
  let poolAddress = acc[9];

  before("setup instances", async() => {
    core = await Core.new();
    await core.initialize(owner);

    privGOP = await PrivGOP.new();

    gop = await GOPCreator.new(owner, privGOP.address, core.address, acc[3], {
      from: owner
    })

    hd = await HorseData.new();
    await hd.initialize();

    breedTypes = await BreedTypes.new();
    await breedTypes.initialize(owner);
    await breedTypes.populateMatrix({
      from: owner
    });

    studService = await StudService.new(core.address, breedTypes.address, acc[2], {
      from: owner
    })

    breeding = await Breeding.new()
    await breeding.initialize(owner, core.address, studService.address, poolAddress,
      breedTypes.address, breeding.address)

    await breedTypes.setBreedingAddress(breeding.address, {
      from: owner
    });

    await core.setBreedingAddr(breeding.address, {
      from: owner
    });
    await core.setGOPCreator(gop.address, {
      from: owner
    });
    await core.setHorseDataAddr(hd.address, {
      from: owner
    });

    await gop.openBatch(1, {
      from: owner
    });
    await gop.createGOP(owner, "genesis horse", {
      from: owner,
      value: horseValue
    }); // 0
  })

  it("should get breed type from parents", async() => {
    await gop.createGOP(owner, "female horse", {
      from: owner,
      value: horseValue
    }); // 1
    await gop.createGOP(owner, "male horse", {
      from: owner,
      value: horseValue
    }); // 2

    await studService.putInStud(2, web3.utils.toWei("1", "ether"), 86400, {
      from: owner
    })

    await breeding.mix(2, 1, "female horse", {
      from: owner,
      value: horseValue
    }); // 3

    // Parents are Genesis so we should have a Legendary
    let breedType = await breedTypes.getBreedType(3);

    assert.equal(web3.utils.toUtf8(breedType), "legendary");
  })
})

