sourcesContent:
const Core = artifacts.require("Core")
const GOPCreator = artifacts.require("GOPCreatorV2")
const StudServiceV3 = artifacts.require("StudServiceV3")
const HorseData = artifacts.require("HorseData")
const BreedTypes = artifacts.require("BreedTypes")
const Breeding = artifacts.require("Breeding")

contract("StudServiceV3", acc => {
  let core, gop, studService, hd, breedTypes, breeding;
  let horseValue = web3.utils.toWei("1", "ether")
  let highMating = web3.utils.toWei("1", "ether")
  let lowMating = web3.utils.toWei("0.004", "ether")
  let queryPrice = web3.utils.toWei("0.2", "ether")
  let seconds = { day1: 86400, day3: 259200, day6: 518400 }
  let owner = acc[1]
  let secondOwner = acc[2]
  let randomAddress = acc[3]
  let poolAddress = acc[9]

  before("Setup instances", async () => {
    core = await Core.new()
    await core.initialize(owner)

    gop = await GOPCreator.new()
    await gop.initialize(core.address, owner)

    hd = await HorseData.new()
    await hd.initialize()

    await core.setGOPCreator(gop.address, { from: owner })
    await core.setHorseDataAddr(hd.address, { from: owner })

    breedTypes = await BreedTypes.new()
    await breedTypes.initialize(owner)

    studService = await StudServiceV3.new()
    await studService.initialize(owner, core.address, breedTypes.address)

    breeding = await Breeding.new()
    await breeding.initialize(owner, core.address, studService.address, poolAddress)

    await breedTypes.populateMatrix({ from: owner })

    await breeding.setBreedTypesAddress(breedTypes.address, { from: owner })

    gop.openBatch(1, { from: owner })
  })

  describe("Initial methods", () => {
    it("should add all horses", async () => {
      await gop.createGOP(owner, "genesis horse", { value: horseValue })
      await gop.createGOP(owner, "1 female hash", { from: owner, value: horseValue })
      await gop.createGOP(owner, "2 male hash", { from: owner, value: horseValue })
      await gop.createGOP(owner, "3 female hash", { from: owner, value: horseValue })
      await gop.createGOP(owner, "4 male hash", { from: owner, value: horseValue })
      await gop.createGOP(owner, "5 female hash", { from: owner, value: horseValue })
      await gop.createGOP(owner, "6 male hash", { from: owner, value: horseValue })
      await gop.createGOP(owner, "7 female hash", { from: owner, value: horseValue })
      await gop.createGOP(owner, "8 male hash", { from: owner, value: horseValue })
    })

    it("should check the emptiness of init state before adding horses to stud", async () => {
      const horsesInStud = await studService.getHorsesInStud()
      assert.equal(horsesInStud.length, 0)
    })

    it("should revert second initialize method call", async () => {
      try {
        await studService.initialize(owner, core.address, breedTypes.address)
      } catch (error) {
        assert.deepEqual(error.reason, "Contract instance has already been initialized")
      }
    })
  })

  describe("Ownership checks", () => {
    it("should check deployer to admin of stud service", async () => {
      const isAdmin = await studService.isStudServiceAdmin(owner)
      assert.deepEqual(isAdmin, true)
    })

    it("should add another address as admin of stud service", async () => {
