      await studService.renounceStudServiceAdmin({ from: secondOwner })

      try {
        await studService.addStudServiceAdmin(secondOwner, { from: randomAddress })
      } catch (err) {
        assert.equal(err.message, "Returned error: VM Exception while processing transaction: revert")
      } finally {
        // Add back second admin for future tests
        await studService.addStudServiceAdmin(secondOwner, { from: owner })
      }
    })
  })

