      } catch (err) {
        assert.equal(err.reason, "SS notOwnerOfHorse")
      }
    })

    it("should revert if we try to put a female horse into stud", async () => {
      try {
        await studService.putInStud(1, highMating, seconds.day6, { from: owner, value: queryPrice })
        assert.fail("Expected revert not received")
      } catch (err) {
        assert.equal(err.reason, "SS horseIsNotMale")
      }
    })

    it("should use a default value if a different time is sent", async () => {
      // We'll use a random value for the duration since only three values are allowed at the moment.
      await studService.putInStud(4, highMating, 123456, { from: owner, value: queryPrice })

      let studInfo = await studService.getStudInfo(4)
      let time = await web3.utils.toBN(studInfo[2]).toNumber()

      // In case we change the time for dev. 1 day on prod.
      const expected = time === 3600 ? 86400 : time

      assert.equal(expected, seconds.day1) // Default to three days.
    })

    it("should remove last horse from the list of horses in stud", async () => {
      // Horse 2 is in Stud at the moment.
      let horses = await studService.getHorsesInStud()
      assert.deepEqual(horses.toString(), "2,6,4")
      assert.deepEqual(horses.length, 3)

      await studService.removeFromStud(4, { from: owner })
      horses = await studService.getHorsesInStud()

      assert.deepEqual(horses.toString(), "2,6")
      assert.deepEqual(horses.length, 2)

      await studService.makeAvailableForStud(4, { from: owner })
    })

    it("should revert when minimum breed price is lower than mating price", async () => {
      try {
        await studService.putInStud(4, lowMating, 1234, { from: owner, value: queryPrice })
      } catch (err) {
        assert.equal(err.reason, "SS matingPriceLowerThanMinimumBreedPrice")
      }
    })

    it("should revert if the horse is in the marketplace", async () => {
      await core.createAuction(12, 4, highMating, { from: owner, value: queryPrice })

