import { expect } from "chai";
import { getTypedData, getRsvFromSig, chainId } from "../../utils";

describe("Core", () => {
  let core, horseData, acc, owner;

  before("populate accs", async () => {
    acc = await ethers.getSigners();
    owner = acc[0];
  });

  beforeEach("get factories", async () => {
    horseData = await ethers.getContractFactory("HorseData");
    core = await ethers.getContractFactory("Core");
  });

  beforeEach("setup instances", async () => {
    horseData = await horseData.deploy();
    await horseData.deployed();

    core = await core.deploy(horseData.address);
    await core.deployed();

    await core.setDomainSeparator("ZED Horse", "1", chainId);

    await core.grantRole(
      ethers.utils.formatBytes32String("core_owners"),
      owner.address
    );
    await core.grantRole(
      ethers.utils.formatBytes32String("core_contracts"),
      owner.address
    );

    // We cannot interact with ID 0
    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Male"),
      ethers.utils.formatBytes32String("FirstName"),
      ethers.utils.formatBytes32String("Black")
    );
  });

  describe("mintCustomHorse", async () => {
    it("should mint a horse with correct attributes", async () => {
      let tx = await core.mintCustomHorse(
        owner.address,
        4,
        ethers.utils.formatBytes32String("Male"),
        ethers.utils.formatBytes32String("Miguel"),
        ethers.utils.formatBytes32String("Blue")
      );

      let data = await core.getHorseData(1);

      expect(ethers.utils.parseBytes32String(data["0"])).to.equal("M");
      expect(data["1"].toNumber()).to.be.above(4);
      expect(data["1"].toNumber()).to.be.below(75);
      expect(data["3"]).to.equal(4);
      expect(ethers.utils.parseBytes32String(data["4"])).to.equal("S");
      expect(ethers.utils.parseBytes32String(data["5"])).to.equal("Colt");
      expect(ethers.utils.parseBytes32String(data["6"])).to.equal("Miguel");
      expect(ethers.utils.parseBytes32String(data["7"])).to.equal("Blue");

      expect(tx).to.emit(core, "Transfer");
      expect(tx)
        .to.emit(core, "LogGOPCreated")
        .withArgs(owner.address, data["2"].toNumber(), 1);

      expect(await core.tokenURI(1)).to.equal(
        "https://api.zed.run/api/v1/horses/metadata/1"
      );
    });

    it("should select the correct range of base value depending on the gen", async () => {
      await core.mintCustomHorse(
        owner.address,
        1,
        ethers.utils.formatBytes32String("Male"),
        ethers.utils.formatBytes32String("Name"),
        ethers.utils.formatBytes32String("Black")
      );

      let baseValue = await core.getBaseValue(1);

      expect(baseValue.toNumber()).to.be.greaterThanOrEqual(95);
      expect(baseValue.toNumber()).to.be.lessThanOrEqual(99);

      await core.mintCustomHorse(
        owner.address,
        2,
        ethers.utils.formatBytes32String("Male"),
        ethers.utils.formatBytes32String("SecondName"),
        ethers.utils.formatBytes32String("Black")
      );

      baseValue = await core.getBaseValue(2);

      expect(baseValue.toNumber()).to.be.lessThanOrEqual(89);
      expect(baseValue.toNumber()).to.be.greaterThanOrEqual(80);
    });

    it("should revert if genotype is out of bounds", async () => {
      expect(
        core.mintCustomHorse(
          owner.address,
          12,
          ethers.utils.formatBytes32String("Male"),
          ethers.utils.formatBytes32String("MyNameIs"),
          ethers.utils.formatBytes32String("Color")
        )
      ).to.be.revertedWith("Core: gen out of bounds");
    });

    it("should revert if name is taken", async () => {
      expect(
        core.mintCustomHorse(
          owner.address,
          1,
          ethers.utils.formatBytes32String("Male"),
          ethers.utils.formatBytes32String("FirstName"),
          ethers.utils.formatBytes32String("Color")
        )
      ).to.be.revertedWith("Core: name already taken");
    });
  });

  describe("mintOffspring", async () => {
    // The 'owner' address has the 'core_contracts' role
    it("should mint an offspring with default name", async () => {
      await core.mintCustomHorse(
        owner.address,
        2,
        ethers.utils.formatBytes32String("Male"),
        ethers.utils.formatBytes32String("Male Name"),
        ethers.utils.formatBytes32String("Color")
      ); // ID 1

      await core.mintCustomHorse(
        owner.address,
        2,
        ethers.utils.formatBytes32String("Female"),
        ethers.utils.formatBytes32String("Female Name"),
        ethers.utils.formatBytes32String("Color")
      ); // ID 2

      await core.mintOffspring(
        owner.address,
        1,
        2,
        ethers.utils.formatBytes32String("The Color")
      );

      let horseData = await core.getHorseData(3);

      expect(ethers.utils.parseBytes32String(horseData["6"])).to.equal(
        "Unnamed Foal"
      );
    });

    it("should mint an offspring with parent's bloodline", async () => {
      await core.mintCustomHorse(
        owner.address,
        2,
        ethers.utils.formatBytes32String("Male"),
        ethers.utils.formatBytes32String("Male Name"),
        ethers.utils.formatBytes32String("Color")
      ); // ID 1

      await core.mintCustomHorse(
        owner.address,
        1,
        ethers.utils.formatBytes32String("Female"),
        ethers.utils.formatBytes32String("Female Name"),
        ethers.utils.formatBytes32String("Color")
      ); // ID 2

      await core.mintOffspring(
        owner.address,
        1,
        2,
        ethers.utils.formatBytes32String("Some Color")
      );

      let horseData = await core.getHorseData(3);

      expect(ethers.utils.parseBytes32String(horseData["4"])).to.equal("N");
    });

    it("should update horse type of parents", async () => {
      await core.mintCustomHorse(
        owner.address,
        2,
        ethers.utils.formatBytes32String("Male"),
        ethers.utils.formatBytes32String("Male Name"),
        ethers.utils.formatBytes32String("Color")
      ); // ID 1

      await core.mintCustomHorse(
        owner.address,
        1,
        ethers.utils.formatBytes32String("Female"),
        ethers.utils.formatBytes32String("Female Name"),
        ethers.utils.formatBytes32String("Color")
      ); // ID 2

      await core.mintOffspring(
        owner.address,
        1,
        2,
        ethers.utils.formatBytes32String("Color")
      );

      let parentData = await core.getHorseData(1);
      let motherData = await core.getHorseData(2);

      expect(ethers.utils.parseBytes32String(parentData["5"])).to.equal(
        "Stallion"
      );
      expect(ethers.utils.parseBytes32String(motherData["5"])).to.equal("Mare");
    });
  });

  describe("set/get", async () => {
    it("should return and set the URI base", async () => {
      let uriBase = await core.baseURI();
      expect(uriBase).to.equal("https://api.zed.run/api/v1/horses/metadata/");

      await core.setBaseURI("MyURIBase");

      uriBase = await core.baseURI();
      expect(uriBase).to.equal("MyURIBase");
    });
  });

  describe("view", async () => {
    it("should return whether or not a token exists", async () => {
      expect(await core.tokenExists(0)).to.be.true;
      expect(await core.tokenExists(1)).to.be.false;

      await core.mintCustomHorse(
        owner.address,
        1,
        ethers.utils.formatBytes32String("Female"),
        ethers.utils.formatBytes32String("Name"),
        ethers.utils.formatBytes32String("Black")
      );

      expect(await core.tokenExists(1)).to.be.true;
    });
  });

  describe("protected functions", async () => {
    it("should allow admin to transfer tokens", async () => {
      expect(await core.ownerOf(0)).to.equal(owner.address);

      let tx = await core.adminTransferToken(0, acc[1].address);

      expect(await core.ownerOf(0)).to.equal(acc[1].address);
      expect(tx).to.emit(core, "Transfer");
    });

    it("should fail if non admin calls tries to transfer", async () => {
      expect(
        core.connect(acc[3]).adminTransferToken(0, acc[1].address)
      ).to.be.revertedWith("Core: unauthorized");
    });

    describe("pausing contract", async () => {
      it("cannot create horses if contract is paused", async () => {
        await core.pause();

        expect(
          core.mintCustomHorse(
            owner.address,
            1,
            ethers.utils.formatBytes32String("Female"),
            ethers.utils.formatBytes32String("Migz"),
            ethers.utils.formatBytes32String("Negro")
          )
        ).to.be.revertedWith("Pausable: paused");
      });

      it("cannot transfer tokens when contract is paused", async () => {
        await core.pause();

        expect(
          core.transferFrom(owner.address, acc[1].address, 0)
        ).to.be.revertedWith("ERC721Pausable: token transfer while paused");
      });
    });

    describe("roles", async () => {
      it("should allow admins to grant admin roles", async () => {
        await core.grantRoleAdmin(
          ethers.utils.formatBytes32String("core_owners_admin"),
          acc[1].address
        );

        expect(
          await core.hasRole(
            ethers.utils.formatBytes32String("core_owners_admin"),
            acc[1].address
          )
        ).to.be.true;
      });

      it("should revert if non-admin wants to grant admin role", async () => {
        expect(
          core
            .connect(acc[2])
            .grantRoleAdmin(
              ethers.utils.formatBytes32String("core_owners_admin"),
              acc[1].address
            )
        ).to.be.revertedWith("Core: unauthorized");
      });

      it("should allow owners admins to grant another type of admin role", async () => {
        await core.grantRoleAdmin(
          ethers.utils.formatBytes32String("core_contracts_admin"),
          acc[1].address
        );

        expect(
          await core.hasRole(
            ethers.utils.formatBytes32String("core_contracts_admin"),
            acc[1].address
          )
        ).to.be.true;
      });

      it("should revert caller does not have core_owners_admin role when grantin role without verification", async () => {
        await core.grantRoleAdmin(
          ethers.utils.formatBytes32String("core_contracts_admin"),
          acc[1].address
        );

        expect(
          core
            .connect(acc[1])
            .grantRoleAdmin(
              ethers.utils.formatBytes32String("core_owners_admin"),
              acc[4].address
            )
        ).to.be.revertedWith("Core: unauthorized");
      });
    });
  });

  describe("mint token with horse data", async () => {
    it("should mint a token with the specified horse data", async () => {
      let tokenId = 5;
      let sex = "M";
      let baseValue = 4;
      let timestamp = Date.now();
      let genotype = 4;
      let bloodline = "S";
      let hType = "Colt";
      let name = "Trung";
      let color = "White";

      let tx = await core.mintTokenWithHorseData(
        owner.address,
        tokenId,
        ethers.utils.formatBytes32String(sex),
        baseValue,
        timestamp,
        genotype,
        ethers.utils.formatBytes32String(bloodline),
        ethers.utils.formatBytes32String(hType),
        ethers.utils.formatBytes32String(name),
        ethers.utils.formatBytes32String(color)
      );

      let data = await core.getHorseData(tokenId);

      expect(ethers.utils.parseBytes32String(data["0"])).to.equal(sex);
      expect(data["1"].toNumber()).to.equal(baseValue);
      expect(data["2"].toNumber()).to.equal(timestamp);
      expect(data["3"].toNumber()).to.equal(genotype);
      expect(ethers.utils.parseBytes32String(data["4"])).to.equal(bloodline);
      expect(ethers.utils.parseBytes32String(data["5"])).to.equal(hType);
      expect(ethers.utils.parseBytes32String(data["6"])).to.equal(name);
      expect(ethers.utils.parseBytes32String(data["7"])).to.equal(color);

      expect(tx).to.emit(core, "Transfer");
      expect(tx).to.emit(core, "LogMintTokenWithHorseData");

      expect(await core.ownerOf(tokenId)).to.equal(owner.address);
    });
  });

  describe("batch mint token with horse data", async () => {
    it("should mint multiple tokens with the specified horse data list", async () => {
      let tokenId = 5;
      let sex = "M";
      let baseValue = 4;
      let timestamp = Date.now();
      let genotype = 4;
      let bloodline = "S";
      let hType = "Colt";
      let hType2 = "Filly";
      let name = "Trung";
      let name2 = "Mila";
      let color = "White";
      let color2 = "Blue";

      let tx = await core.mintTokenWithHorseDataBatch(
        [owner.address, acc[1].address],
        [tokenId, tokenId + 1],
        [
          ethers.utils.formatBytes32String(sex),
          ethers.utils.formatBytes32String(sex),
        ],
        [baseValue, baseValue + 1],
        [timestamp, timestamp + 1000],
        [genotype, genotype + 1],
        [
          ethers.utils.formatBytes32String(bloodline),
          ethers.utils.formatBytes32String(bloodline),
        ],
        [
          ethers.utils.formatBytes32String(hType),
          ethers.utils.formatBytes32String(hType2),
        ],
        [
          ethers.utils.formatBytes32String(name),
          ethers.utils.formatBytes32String(name2),
        ],
        [
          ethers.utils.formatBytes32String(color),
          ethers.utils.formatBytes32String(color2),
        ]
      );

      let data = await core.getHorseData(tokenId);

      expect(ethers.utils.parseBytes32String(data["0"])).to.equal(sex);
      expect(data["1"].toNumber()).to.equal(baseValue);
      expect(data["2"].toNumber()).to.equal(timestamp);
      expect(data["3"].toNumber()).to.equal(genotype);
      expect(ethers.utils.parseBytes32String(data["4"])).to.equal(bloodline);
      expect(ethers.utils.parseBytes32String(data["5"])).to.equal(hType);
      expect(ethers.utils.parseBytes32String(data["6"])).to.equal(name);
      expect(ethers.utils.parseBytes32String(data["7"])).to.equal(color);

      data = await core.getHorseData(tokenId + 1);

      expect(ethers.utils.parseBytes32String(data["0"])).to.equal(sex);
      expect(data["1"].toNumber()).to.equal(baseValue + 1);
      expect(data["2"].toNumber()).to.equal(timestamp + 1000);
      expect(data["3"].toNumber()).to.equal(genotype + 1);
      expect(ethers.utils.parseBytes32String(data["4"])).to.equal(bloodline);
      expect(ethers.utils.parseBytes32String(data["5"])).to.equal(hType2);
      expect(ethers.utils.parseBytes32String(data["6"])).to.equal(name2);
      expect(ethers.utils.parseBytes32String(data["7"])).to.equal(color2);

      expect(tx).to.emit(core, "Transfer");
      expect(tx).to.emit(core, "LogMintTokenWithHorseDataBatch");

      expect(await core.ownerOf(tokenId + 1)).to.equal(acc[1].address);

      // Change horse name
      const newName = "Trung New";
      const newName2 = "Trung New 2";
      tx = await core.setHorseNameBatch(
        [tokenId, tokenId + 1],
        [
          ethers.utils.formatBytes32String(newName),
          ethers.utils.formatBytes32String(newName2),
        ]
      );
      data = await core.getHorseData(tokenId);
      expect(ethers.utils.parseBytes32String(data["6"])).to.equal(newName);
      data = await core.getHorseData(tokenId + 1);
      expect(ethers.utils.parseBytes32String(data["6"])).to.equal(newName2);

      expect(tx).to.emit(core, "LogSetHorseNameBatch");

      // Test name uniqueness
      expect(
        core.setHorseNameBatch(
          [tokenId, tokenId + 1],
          [
            ethers.utils.formatBytes32String(newName),
            ethers.utils.formatBytes32String(newName2),
          ]
        )
      ).to.be.revertedWith("Core: name already taken");
    });

    it("should not mint anything with empty data list", async () => {
      let tokenId = 5;
      let tx = await core.mintTokenWithHorseDataBatch(
        [],
        [],
        [],
        [],
        [],
        [],
        [],
        [],
        [],
        []
      );

      expect(tx).to.emit(core, "LogMintTokenWithHorseDataBatch");

      expect(await core.tokenExists(tokenId)).to.be.false;
    });
  });

  describe("burning", async () => {
    it("burns a token and does not affect the next token ID", async () => {
      await core.mintCustomHorse(
        owner.address,
        1,
        ethers.utils.formatBytes32String("Female"),
        ethers.utils.formatBytes32String("VHS"),
        ethers.utils.formatBytes32String("Negro")
      );

      let horseData = await core.getHorseData(1);

      expect(ethers.utils.parseBytes32String(horseData["7"])).to.equal("Negro");

      expect(await core.tokenExists(1)).to.be.true;
      expect((await core.nextTokenId()).toNumber()).to.equal(2);

      await core.burn(1);

      horseData = await core.getHorseData(1);

      expect((await core.nextTokenId()).toNumber()).to.equal(2);
      expect(ethers.utils.parseBytes32String(horseData["7"])).to.not.equal(
        "Negro"
      );

      // The total supply is affected, not the next token ID
      expect((await core.totalSupply()).toNumber()).to.equal(1);
    });

    it("reverts if sender is not owner of token", async () => {
      expect(core.connect(acc[1]).burn(0)).to.be.revertedWith(
        "Core: not owner of token"
      );
    });

    it("reverts if token does not exist", async () => {
      expect(core.burn(1)).to.be.revertedWith("Core: token does not exist");
    });
  });

  describe("setting name", async () => {
    it("sets the name of the horse", async () => {
      await core.mintCustomHorse(
        owner.address,
        1,
        ethers.utils.formatBytes32String("Male"),
        ethers.utils.formatBytes32String("VHS"),
        ethers.utils.formatBytes32String("Black")
      );

      await core.setHorseName(1, ethers.utils.formatBytes32String("New Name"));

      let horseData = await core.getHorseData(1);

      expect(ethers.utils.parseBytes32String(horseData["6"])).to.equal(
        "New Name"
      );
    });

    it("reverts if name is already taken", async () => {
      // Token 0 from setup's name is FirstName
      expect(
        core.setHorseName(0, ethers.utils.formatBytes32String("FirstName"))
      ).to.be.revertedWith("Core: name already taken");
    });
  });

  describe("meta-transactions", async () => {
    it("should create a horse through a meta-tx", async () => {
      let functionSignature = core.interface.encodeFunctionData(
        "mintCustomHorse",
        [
          owner.address,
          3,
          ethers.utils.formatBytes32String("Male"),
          ethers.utils.formatBytes32String("SecondName"),
          ethers.utils.formatBytes32String("Negro"),
        ]
      );

      let typedData = await getTypedData({
        name: "ZED Horse",
        version: "1",
        chainId: chainId,
        verifyingContract: core.address,
        nonce: await core.getNonce(owner.address),
        from: owner.address,
        functionSignature: functionSignature,
      });

      let signedData = await owner._signTypedData(
        typedData.domain,
        typedData.types,
        typedData.message
      );

      let { r, s, v } = await getRsvFromSig(signedData);

      await core.executeMetaTransaction(
        owner.address,
        functionSignature,
        r,
        s,
        v
      );

      let data = await core.getHorseData(1);

      expect(ethers.utils.parseBytes32String(data["0"])).to.equal("M");
      expect(data["1"].toNumber()).to.be.above(4);
      expect(data["1"].toNumber()).to.be.below(80);
      expect(data["3"].toNumber()).to.equal(3);
      expect(ethers.utils.parseBytes32String(data["4"])).to.equal("S");
      expect(ethers.utils.parseBytes32String(data["5"])).to.equal("Colt");
      expect(ethers.utils.parseBytes32String(data["6"])).to.equal("SecondName");
      expect(ethers.utils.parseBytes32String(data["7"])).to.equal("Negro");
    });

    it("should transfer a horse through a meta-tx", async () => {
      // Since ID 0 is created already we're going to use that one
      let functionSignature = core.interface.encodeFunctionData(
        "transferFrom",
        [owner.address, acc[1].address, 0]
      );

      let typedData = await getTypedData({
        name: "ZED Horse",
        version: "1",
        chainId: chainId,
        verifyingContract: core.address,
        nonce: await core.getNonce(owner.address),
        from: owner.address,
        functionSignature: functionSignature,
      });

      let signedData = await owner._signTypedData(
        typedData.domain,
        typedData.types,
        typedData.message
      );
      let { r, s, v } = await getRsvFromSig(signedData);

      await core.executeMetaTransaction(
        owner.address,
        functionSignature,
        r,
        s,
        v
      );

      expect(await core.ownerOf(0)).to.equal(acc[1].address);
    });

    it("should revert if user has no rights for token transfer", async () => {
      let functionSignature = core.interface.encodeFunctionData(
        "transferFrom",
        [owner.address, acc[1].address, 0]
      );

      let typedData = await getTypedData({
        name: "ZED Horse",
        version: "1",
        chainId: chainId,
        verifyingContract: core.address,
        nonce: await core.getNonce(acc[1].address),
        from: acc[1].address,
        functionSignature: functionSignature,
      });

      let signedData = await acc[1]._signTypedData(
        typedData.domain,
        typedData.types,
        typedData.message
      );
      let { r, s, v } = await getRsvFromSig(signedData);

      expect(
        core.executeMetaTransaction(acc[1].address, functionSignature, r, s, v)
      ).to.be.revertedWith("ERC721: transfer caller is not owner nor approved");
    });

    it("should burn a token for an user", async () => {
      await core.mintCustomHorse(
        acc[4].address,
        1,
        ethers.utils.formatBytes32String("Female"),
        ethers.utils.formatBytes32String("FemHorse"),
        ethers.utils.formatBytes32String("Pink")
      );

      expect(await core.tokenExists(1)).to.be.true;

      let functionSignature = core.interface.encodeFunctionData("burn", [1]);

      let typedData = await getTypedData({
        name: "ZED Horse",
        version: "1",
        chainId: chainId,
        verifyingContract: core.address,
        nonce: await core.getNonce(acc[4].address),
        from: acc[4].address,
        functionSignature: functionSignature,
      });

      let signedData = await acc[4]._signTypedData(
        typedData.domain,
        typedData.types,
        typedData.message
      );
      let { r, s, v } = await getRsvFromSig(signedData);

      await core.executeMetaTransaction(
        acc[4].address,
        functionSignature,
        r,
        s,
        v
      );

      expect(await core.tokenExists(1)).to.be.false;
      expect(await core.nextTokenId()).to.equal(2);
    });
  });
});

