import { getTypedData, getRsvFromSig, chainId } from "../../utils";
import { expect } from "chai";

const STUD_OWNERS_ROLE = ethers.utils.formatBytes32String("stud_owners");

describe("StudServiceV5", () => {
  let core,
    breedTypes,
    breedingLogic,
    breedingProxy,
    breedingProxyAdmin,
    breeding,
    studService,
    horseData,
    erc20;

  let acc,
    owner,
    secondOwner,
    randomAddress,
    poolAddress,
    highMating = ethers.utils.parseUnits("1"),
    seconds = {
      day1: 86400,
      day3: 259200,
      day6: 518400,
    },
    amount = ethers.utils.parseUnits("0.1"),
    maxWETH = ethers.utils.parseUnits("100");

  before("populate accs", async () => {
    acc = await ethers.getSigners();
    owner = acc[0];
    secondOwner = acc[2];
    randomAddress = acc[3];
    poolAddress = acc[9];
  });

  beforeEach("get factories", async () => {
    horseData = await ethers.getContractFactory("HorseData");
    core = await ethers.getContractFactory("Core");
    breedTypes = await ethers.getContractFactory("BreedTypes");
    studService = await ethers.getContractFactory("StudServiceV5");
    breedingProxyAdmin = await ethers.getContractFactory("ZedProxyAdmin");
    breedingProxy = await ethers.getContractFactory("BreedingProxy");
    breeding = await ethers.getContractFactory("Breeding");
    erc20 = await ethers.getContractFactory("ERC20Test");
  });

  beforeEach("deploy instances", async () => {
    horseData = await horseData.deploy();
    await horseData.deployed();

    core = await core.deploy(horseData.address);
    await core.deployed();

    breedTypes = await breedTypes.deploy();
    await breedTypes.deployed();

    studService = await studService.deploy(
      core.address,
      horseData.address,
      breedTypes.address
    );
    await studService.deployed();

    erc20 = await erc20.deploy(ethers.utils.parseUnits("100"));
    await erc20.deployed();

    // Deploys Breeding Logic instance
    breedingLogic = await breeding.deploy();
    await breedingLogic.deployed();

    // Deploys Breeding Admin to be used as admin of BreedingProxy
    breedingProxyAdmin = await breedingProxyAdmin.deploy();
    await breedingProxyAdmin.deployed();

    // Deploys proxy instance
    breedingProxy = await breedingProxy.deploy(
      breedingLogic.address,
      breedingProxyAdmin.address
    );
    await breedingProxy.deployed();

    breeding = new ethers.Contract(
      breedingProxy.address,
      breedingLogic.interface
    );

    breeding = breeding.connect(owner);
  });

  beforeEach("setup initial state of instances", async () => {
    await studService.grantRole(
      ethers.utils.formatBytes32String("stud_owners"),
      owner.address
    );

    await studService.setDomainSeparator("ZED Stud", "1", chainId);
    await core.grantRole(
      ethers.utils.formatBytes32String("core_contracts"),
      owner.address
    );

    // We cannot interact with ID 0
    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Male"),
      ethers.utils.formatBytes32String("FirstName"),
      ethers.utils.formatBytes32String("Black")
    );

    await breedTypes.grantRole(
      ethers.utils.formatBytes32String("breed_types_owners"),
      owner.address
    );

    await breeding.initialize(
      core.address,
      studService.address,
      breedTypes.address,
      erc20.address,
      poolAddress.address,
      acc[2].address
    );

    await breedTypes.grantRole(
      ethers.utils.formatBytes32String("breed_types_contracts"),
      breeding.address
    );

    // Core need to grant CORE_CONTRACTS_ROLE for Breeding to able to invoke method Core.mintOffspring
    await core.grantRole(
      ethers.utils.formatBytes32String("core_contracts"),
      breeding.address
    );

    await erc20.transfer(acc[2].address, amount);
    await erc20.connect(secondOwner).approve(breeding.address, maxWETH);
    await erc20.approve(breeding.address, maxWETH);

    await createInitialHorses();
  });

  const createInitialHorses = async () => {
    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Female"),
      ethers.utils.formatBytes32String("Truc1"),
      ethers.utils.formatBytes32String("violet")
    );

    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Male"),
      ethers.utils.formatBytes32String("Trung1"),
      ethers.utils.formatBytes32String("blue")
    );

    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Female"),
      ethers.utils.formatBytes32String("Truc2"),
      ethers.utils.formatBytes32String("violet")
    );

    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Male"),
      ethers.utils.formatBytes32String("Trung2"),
      ethers.utils.formatBytes32String("blue")
    );

    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Male"),
      ethers.utils.formatBytes32String("Trung3"),
      ethers.utils.formatBytes32String("blue")
    );
  };

  describe("Initial methods", () => {
    it("should check the emptiness of init state before adding horses to stud", async () => {
      expect(await studService.getHorsesInStud()).to.be.empty;
    });
  });

  describe("Ownership checks", () => {
    it("should check deployer to admin of stud service", async () => {
      expect(await studService.hasRole(STUD_OWNERS_ROLE, owner.address)).to.be
        .true;
    });

    it("should add another address as admin of stud service", async () => {
      await studService.grantRole(STUD_OWNERS_ROLE, secondOwner.address);

      expect(await studService.hasRole(STUD_OWNERS_ROLE, secondOwner.address))
        .to.be.true;
    });

    it("should revert transaction to protected method from non-registered addresses", async () => {
      expect(
        studService
          .connect(randomAddress)
          .grantRole(STUD_OWNERS_ROLE, secondOwner.address)
      ).to.be.revertedWith("AccessControl: sender must be an admin to grant");
    });

    it("should renounce ownership and can't call methods after that", async () => {
      await studService.revokeRole(STUD_OWNERS_ROLE, secondOwner.address);

      expect(
        studService
          .connect(secondOwner)
          .grantRole(STUD_OWNERS_ROLE, randomAddress.address)
      ).to.be.revertedWith("AccessControl: sender must be an admin to grant");
    });
  });

  describe("Stud admin methods checks", () => {
    it("should add new timeframe and check the state", async () => {
      const newTimeframe = 256000;
      let isNewTimeframeActive = await studService.isTimeframeExist(
        newTimeframe
      );

      expect(isNewTimeframeActive).to.be.false;

      await studService.addTimeFrame(newTimeframe);

      isNewTimeframeActive = await studService.isTimeframeExist(newTimeframe);
      expect(isNewTimeframeActive).to.be.true;
    });

    it("should remove timeframe and check the state", async () => {
      const targetTimeframe = 86400;
      await studService.removeTimeFrame(targetTimeframe);

      const isTargetTimeframeActive = await studService.isTimeframeExist(
        targetTimeframe
      );
      expect(isTargetTimeframeActive).to.be.false;
    });

    it("should set new base fee", async () => {
      let currentBaseFee = await studService.getBaseFee();
      expect(currentBaseFee).to.equal(ethers.utils.parseUnits("0.075"));

      await studService.setBaseFee(ethers.utils.parseUnits("0.5"));
      currentBaseFee = await studService.getBaseFee();

      expect(currentBaseFee.toString()).to.equal(
        ethers.utils.parseUnits("0.5")
      );
    });

    it("should set new breed type weight", async () => {
      const breedType = ethers.utils.formatBytes32String("mustang");
      let newBreedTypeWeight = await studService.getBreedTypeWeight(breedType);
      expect(newBreedTypeWeight.toString()).to.equal("0");

      await studService.setBreedTypeWeight(breedType, 200);

      newBreedTypeWeight = await studService.getBreedTypeWeight(breedType);
      expect(newBreedTypeWeight.toString()).to.equal("200");
    });

    it("should set new bloodline weight", async () => {
      const bloodLine = ethers.utils.formatBytes32String("X");
      let newBloodLineWeight = await studService.getBloodlineWeight(bloodLine);
      expect(newBloodLineWeight.toString()).to.equal("0");

      await studService.setBloodlineWeight(bloodLine, 200);

      newBloodLineWeight = await studService.getBloodlineWeight(bloodLine);
      expect(newBloodLineWeight.toString()).to.equal("200");
    });

    it("should allow admin to put and remove horse on stud without value", async () => {
      await studService.adminPutInStud(2, highMating, seconds.day1);

      expect(await studService.isHorseInStud(2)).to.be.true;
      expect(await studService.getMatingPrice(2)).to.equal(highMating);
      expect(await studService.getStudTime(2)).to.equal(seconds.day1);

      await studService.removeFromStud(2);

      expect(await studService.isHorseInStud(2)).to.be.false;
      expect(await studService.getMatingPrice(2)).to.equal(0);
      expect(await studService.getStudTime(2)).to.equal(0);
    });
  });

  describe("Stud logic checks", () => {
    it("should add first horse in stud", async () => {
      await studService.putInStud(2, highMating, seconds.day1);

      const studInfo = await studService.getStudInfo(2);
      expect(studInfo[0]).to.be.true;
      expect(studInfo[1]).to.equal(highMating);
      expect(studInfo[2]).to.equal(seconds.day1);
    });

    it("should add 2 horses to stud", async () => {
      // Add horses to stud
      await studService.putInStud(2, highMating, seconds.day1);
      await studService.putInStud(4, highMating, seconds.day3);

      // Get state of id4 horse
      let studInfo = await studService.getStudInfo(4);
      expect(studInfo[0]).to.be.true;
      expect(studInfo[1]).to.equal(highMating);
      expect(studInfo[2]).to.equal(seconds.day3);

      let horsesInStud = await studService.getHorsesInStud();

      expect(horsesInStud.toString()).to.equal("2,4");
    });

    it("should remove horse from stud and update listed horses", async () => {
      await studService.putInStud(4, highMating, seconds.day1);
      await studService.putInStud(2, highMating, seconds.day1);

      // Removes horse from Stud list and changes status. It can't be placed into Stud while
      // the specified time has not passed yet (should be tested on the next test)
      await studService.removeFromStud(4);

      const horsesInStud = await studService.getHorsesInStud();
      expect(horsesInStud.toString()).to.equal("2");
    });

    it("should use a default value if a different time is sent", async () => {
      await core.mintCustomHorse(
        randomAddress.address,
        4,
        ethers.utils.formatBytes32String("Male"),
        ethers.utils.formatBytes32String("FirstName111"),
        ethers.utils.formatBytes32String("Black")
      );

      // We'll use a random value for the duration since only three values are allowed at the moment.
      await studService.connect(randomAddress).putInStud(6, highMating, 123456);

      let studInfo = await studService.getStudInfo(6);

      // In case we change the time for dev. 1 day on prod.
      const expectedTime =
        studInfo[2].toNumber() === 3600 ? 86400 : studInfo[2].toNumber();

      expect(expectedTime).to.equal(seconds.day1); // Default to three days.
    });

    it("should remove last horse from the list of horses in stud", async () => {
      await studService.putInStud(2, highMating, seconds.day1);
      await studService.putInStud(4, highMating, seconds.day1);

      // Horse 2 is in Stud at the moment.
      let horses = await studService.getHorsesInStud();
      expect(horses.toString()).to.equal("2,4");

      await studService.removeFromStud(4);
      horses = await studService.getHorsesInStud();

      expect(horses.toString()).to.equal("2");
    });
  });

  describe("reverts", async () => {
    describe("putInStud", async () => {
      it("should revert when a different user than owner tries to put a horse in stud", async () => {
        expect(
          studService
            .connect(randomAddress)
            .putInStud(4, highMating, seconds.day6)
        ).to.be.revertedWith("StudService: unauthorized");
      });

      it("should revert if we try to put a female horse into stud", async () => {
        expect(
          studService.putInStud(1, highMating, seconds.day6)
        ).to.be.revertedWith("StudService: horse is not male");
      });

      it("should revert if minimum breed price is lower than required", async () => {
        expect(
          studService.putInStud(
            2,
            ethers.utils.parseUnits("0.0001"),
            seconds.day1
          )
        ).to.be.revertedWith(
          "StudService: mating price lower than minimum breed price"
        );
      });
    });

    describe("pausable", async () => {
      it("can only be paused by accounts with correct role", async () => {
        await studService.pause();
        await studService.unpause();

        expect(studService.connect(acc[1]).pause()).to.be.revertedWith(
          "StudService: unauthorized owner admin"
        );
      });

      it("should revert if paused", async () => {
        expect(await studService.paused()).to.be.false;
        await studService.pause();
        expect(await studService.paused()).to.be.true;

        const minBreedingPrice = await studService.getMinimumBreedPrice(2);
        expect(
          studService.putInStud(2, minBreedingPrice, seconds.day1)
        ).to.be.revertedWith("Pausable: paused");
      });
    });

    describe("addTimeFrame", async () => {
      it("reverts if timeframe is not > 0", async () => {
        expect(studService.addTimeFrame(0)).to.be.revertedWith(
          "StudService: invalid seconds frame"
        );
      });

      it("reverts if timeframe already exists", async () => {
        expect(studService.addTimeFrame(86400)).to.be.revertedWith(
          "StudService: seconds frame already active"
        );
      });
    });

    describe("setBreedTypesAddress", async () => {
      it("reverts if breedtypes address is 0", async () => {
        expect(
          studService.setBreedTypesAddress(ethers.constants.AddressZero)
        ).to.be.revertedWith(
          "StudService: invalid breedtypes contract address"
        );
      });
    });

    describe("setCoreAddress", async () => {
      it("reverts if core address is 0", async () => {
        expect(
          studService.setCoreAddress(ethers.constants.AddressZero)
        ).to.be.revertedWith("StudService: invalid core contract address");
      });
    });

    describe("removeTimeFrame", async () => {
      it("reverts if timeframe is not found when removing", async () => {
        expect(studService.removeTimeFrame(123)).to.be.revertedWith(
          "StudService: seconds frame is not found"
        );
      });
    });
  });

  describe("meta-transactions", async () => {
    it("should put a horse in stud", async () => {
      expect(await studService.isHorseInStud(2)).to.be.false;

      let functionSignature = studService.interface.encodeFunctionData(
        "putInStud",
        [2, highMating, seconds.day3]
      );

      let typedData = await getTypedData({
        name: "ZED Stud",
        version: "1",
        chainId: chainId,
        verifyingContract: studService.address,
        nonce: await studService.getNonce(owner.address),
        from: owner.address,
        functionSignature: functionSignature,
      });

      let signedData = await owner._signTypedData(
        typedData.domain,
        typedData.types,
        typedData.message
      );
      let { r, s, v } = await getRsvFromSig(signedData);

      await studService.executeMetaTransaction(
        owner.address,
        functionSignature,
        r,
        s,
        v
      );

      expect(await studService.isHorseInStud(2)).to.be.true;
    });

    describe("reverts", async () => {
      it("should revert if minimum breed is not met", async () => {
        let functionSignature = studService.interface.encodeFunctionData(
          "putInStud",
          [2, ethers.utils.parseUnits("0.0001"), seconds.day3]
        );

        expect(await studService.isHorseInStud(2)).to.be.false;

        let typedData = await getTypedData({
          name: "ZED Stud",
          version: "1",
          chainId: chainId,
          verifyingContract: studService.address,
          nonce: await studService.getNonce(owner.address),
          from: owner.address,
          functionSignature: functionSignature,
        });

        let signedData = await owner._signTypedData(
          typedData.domain,
          typedData.types,
          typedData.message
        );
        let { r, s, v } = await getRsvFromSig(signedData);

        expect(
          studService.executeMetaTransaction(
            owner.address,
            functionSignature,
            r,
            s,
            v
          )
        ).to.be.revertedWith(
          "StudService: mating price lower than minimum breed price"
        );
      });
    });
  });
