import {
  getTypedData,
  getRsvFromSig,
  increaseTimeBySeconds,
  mineBlock,
  chainId,
} from "../../utils";

import { expect } from "chai";

const DEFAULT_COLOR = ethers.utils.formatBytes32String("Black");

describe("Breeding", () => {
  let core,
    breedTypes,
    breedingLogic,
    breedingLogicInstance,
    breedingProxy,
    breedingProxyAdmin,
    breeding,
    studService,
    horseData,
    erc20;

  let acc,
    owner,
    poolAddress,
    blockchainBrain,
    amount = ethers.utils.parseUnits("0.2"),
    priceyAmount = ethers.utils.parseUnits("1"),
    maxWETH = ethers.utils.parseUnits("1000");

  before("populate accs", async () => {
    acc = await ethers.getSigners();
    owner = acc[0];
    poolAddress = acc[1];
    blockchainBrain = acc[2];
  });

  beforeEach("get factories", async () => {
