        await racing.connect(blockchainBrain).registerHorse(raceId, 2, 5);

        await racing.connect(blockchainBrain).cancelRace(raceId, "Cancel");

        // balances should be the same
        let currentOwnerBalance = await erc20.balanceOf(owner.address);
        let currentBBBalance = await erc20.balanceOf(blockchainBrain.address);

