        await racing.connect(blockchainBrain).cancelRace(raceId, "Cancel");

        expect(
          racing.connect(blockchainBrain).cancelRace(raceId, "take 2")
        ).to.be.revertedWith("Racing: race is not in registration state");
      });
    });
  });

