      await racing.grantRole(
        ethers.utils.formatBytes32String("racing_owners"),
        blockchainBrain.address
      );

      await createRace(raceId);
    });

    describe("valid nomination", async () => {
      it("should nominate a horse without paying anything", async () => {
        const nominator = acc[9].address;
        const balanceBefore = await erc20.balanceOf(nominator);

        await racing.grantRole(
          ethers.utils.formatBytes32String("racing_owners"),
          nominator
        );
        let tx = await racing.connect(acc[9]).adminRegisterHorse(raceId, 1, 2);

        const currentBalance = await erc20.balanceOf(nominator);

        expect(currentBalance).to.equal(balanceBefore);

        // The nominator remains the owner of the horse nonetheless
        expect(tx)
          .to.emit(racing, "HorseRegistered")
          .withArgs(raceId, owner.address, 1, 2);
      });

      it("should not consider active races limit", async () => {
        const raceId2 = ethers.utils.formatBytes32String("race_id_2");
        const raceId3 = ethers.utils.formatBytes32String("race_id_3");
        const raceId4 = ethers.utils.formatBytes32String("race_id_4");

        await createRace(raceId2);
        await createRace(raceId3);
        await createRace(raceId4);

        await racing.connect(blockchainBrain).adminRegisterHorse(raceId, 4, 12);
        await racing.connect(blockchainBrain).adminRegisterHorse(raceId2, 4, 3);
        await racing.connect(blockchainBrain).adminRegisterHorse(raceId3, 4, 5);

        // No trouble registering for a 4th race
        await racing.connect(blockchainBrain).adminRegisterHorse(raceId4, 4, 2);

        expect(await racing.horseActiveRaces(4)).to.equal(4);
      });
    });

    describe("reverts", async () => {
      it("should revert if race is not on registration state", async () => {
        await racing.connect(blockchainBrain).cancelRace(raceId, "reason");

        expect(
          racing.connect(blockchainBrain).adminRegisterHorse(raceId, 1, 1)
        ).to.be.revertedWith("Racing: race not accepting registrations");
      });

      it("should revert if all horses are registered", async () => {
        newTokenIds.forEach(async (tokenId) => {
          // The token ID can be used as the gate number here as well
          await racing.connect(owner).registerHorse(raceId, tokenId, tokenId);
        });

        expect(
          racing.connect(blockchainBrain).adminRegisterHorse(raceId, 1, 1)
        ).to.be.revertedWith("Racing: max number of horses for race");
      });

      it("should revert if gate is lower than 0", async () => {
        expect(
          racing.connect(blockchainBrain).adminRegisterHorse(raceId, 1, 0)
        ).to.be.revertedWith("Racing: gate number lower than 1");
      });

      it("should revert if gate is greater than horses allowed", async () => {
        expect(
          racing.connect(blockchainBrain).adminRegisterHorse(raceId, 1, 13)
        ).to.be.revertedWith("Racing: gate number greater than horses allowed");
      });

      it("should revert if gate is already taken", async () => {
        await racing.connect(blockchainBrain).adminRegisterHorse(raceId, 1, 2);

        expect(
          racing.connect(blockchainBrain).adminRegisterHorse(raceId, 4, 2)
        ).to.be.revertedWith("Racing: gate number already taken");
