        await racing.connect(blockchainBrain).adminRegisterHorse(raceId, 4, 1);

        expect(
          racing.connect(blockchainBrain).adminRegisterHorse(raceId, 4, 5)
        ).to.be.revertedWith("Racing: horse already registered for this race");
      });
    });
  });

