      it("should register a horse", async () => {
        let functionSignature = racing.interface.encodeFunctionData(
          "registerHorse",
          [raceId, 1, 10]
        );

        let typedData = await getTypedData({
          name: "ZED Racing",
          version: "1",
          chainId: chainId,
          verifyingContract: racing.address,
          nonce: await racing.getNonce(owner.address),
          from: owner.address,
          functionSignature: functionSignature,
        });

        let signedData = await owner._signTypedData(
          typedData.domain,
          typedData.types,
          typedData.message
        );
        let { r, s, v } = await getRsvFromSig(signedData);

        let tx = await racing.executeMetaTransaction(
          owner.address,
          functionSignature,
          r,
          s,
          v
        );

        expect(tx)
          .to.emit(racing, "HorseRegistered")
          .withArgs(raceId, owner.address, 1, 10);
      });
    });
  });

  describe("pausing", async () => {
    beforeEach("grant role", async () => {
      await racing.grantRoleAdmin(
        ethers.utils.formatBytes32String("racing_owners"),
        blockchainBrain.address
      );
    });

    it("should pause the contract", async () => {
      await racing.connect(blockchainBrain).pause();
    });

    describe("nomination", async () => {
      beforeEach("create 1 horse", async () => {
        // ID 1
        await core.mintCustomHorse(
          owner.address,
          1,
          ethers.utils.formatBytes32String("Male"),
          ethers.utils.formatBytes32String("Fixed name"),
          ethers.utils.formatBytes32String("Color")
        );
      });

      beforeEach("pause contract", async () => {
        await racing.connect(blockchainBrain).pause();
      });

      it("should revert if contract is paused", async () => {
        expect(
          racing.connect(owner).registerHorse(raceId, 1, 10)
        ).to.be.revertedWith("Pausable: paused");
      });
