      await racing.grantRoleAdmin(
        ethers.utils.formatBytes32String("racing_owners"),
        blockchainBrain.address
      );
    });

    it("should unpause the contract", async () => {
      await racing.connect(blockchainBrain).pause();
      await racing.connect(blockchainBrain).unpause();
    });
  });

  describe("change fee receiver", async () => {
    beforeEach("grant role", async () => {
      await racing.grantRoleAdmin(
        ethers.utils.formatBytes32String("racing_owners"),
        blockchainBrain.address
      );
    });

    it("should change the fee receiver", async () => {
      expect(await racing.feeReceiver()).to.equal(feeReceiver.address);

      await racing.connect(blockchainBrain).changeFeeReceiver(acc[4].address);

      expect(await racing.feeReceiver()).to.equal(acc[4].address);
    });
  });

