const { expect } = require("chai");

describe("WinningsWatcher", () => {
  const MAX_WETH = ethers.utils.parseUnits("10");
  const RACE_ID = ethers.utils.formatBytes32String("race_id");
  const HORSE_ID = 123;

  let transferAmount = ethers.utils.parseUnits("0.005");
  let winningsWatcher, erc20, acc;

  before("setup accs", async () => {
    acc = await ethers.getSigners();
  });

  beforeEach("setup instances", async () => {
    erc20 = await ethers.getContractFactory("ERC20Test");
    erc20 = await erc20.deploy(MAX_WETH);

    winningsWatcher = await ethers.getContractFactory("WinningsWatcher");
    winningsWatcher = await winningsWatcher.deploy(erc20.address);
  });

  describe("sendWinnings", async () => {
    describe("valid transfer", async () => {
      beforeEach("approve weth", async () => {
        await erc20.approve(winningsWatcher.address, MAX_WETH);
      });

      it("should transfer WETH to user", async () => {
        let preBalance = await erc20.balanceOf(acc[0].address);
        await winningsWatcher.sendWinnings(
          RACE_ID,
          acc[1].address,
          HORSE_ID,
          transferAmount
        );

        let currentBalance = await erc20.balanceOf(acc[0].address);
        expect(Number(currentBalance) < Number(preBalance)).to.be.true;
      });

      it("should be able to make transfers to same user with different horse ID", async () => {
        let preBalance = await erc20.balanceOf(acc[0].address);
        await winningsWatcher.sendWinnings(
          RACE_ID,
          acc[1].address,
          HORSE_ID,
          transferAmount
        );
        await winningsWatcher.sendWinnings(
          RACE_ID,
          acc[1].address,
          321,
          transferAmount
        );

        let currentBalance = await erc20.balanceOf(acc[0].address);
        expect(Number(currentBalance) < Number(preBalance)).to.be.true;
      });

      it("should emit event when transfer is made", async () => {
        let tx = await winningsWatcher.sendWinnings(
          RACE_ID,
          acc[1].address,
          HORSE_ID,
          transferAmount
        );

        expect(tx)
          .to.emit(winningsWatcher, "WinningsTransfer")
          .withArgs(RACE_ID, acc[1].address, HORSE_ID, transferAmount);
      });
    });

    describe("reverts", async () => {
      beforeEach("approve weth", async () => {
        await erc20.approve(winningsWatcher.address, MAX_WETH);
      });

      it("should revert if transfer for user in race has been made", async () => {
        await winningsWatcher.sendWinnings(
          RACE_ID,
          acc[1].address,
          HORSE_ID,
          transferAmount
        );

        expect(
          winningsWatcher.sendWinnings(
            RACE_ID,
            acc[1].address,
            HORSE_ID,
            transferAmount
          )
        ).to.be.revertedWith("WinningsWatcher: user has been paid already");
      });
    });
  });
});

