const ganache = require("ganache-core");
const Web3 = require("web3");

import { expect } from "chai";

import { getTypedData, getRsvFromSig, chainId } from "../../utils";

describe("GOPFundsReceiverWETH", () => {
  let acc,
    owner,
    fundsReceiver,
    nextFundsReceiver,
    blockchainBrain,
    bob,
    sasha,
    emily;

  const maxWETH = ethers.utils.parseUnits("1");

  // Contracts instances
  let wethFundsReceiver, erc20, horseData, core;
  let amount = ethers.utils.parseUnits("0.05");

  before("populate accs", async () => {
    acc = await ethers.getSigners();
    owner = acc[0];
    fundsReceiver = acc[1];
    nextFundsReceiver = acc[2];
    blockchainBrain = acc[3];
    bob = acc[4];
    sasha = acc[5];
    emily = acc[6];
  });

  beforeEach("get factories", async () => {
    erc20 = await ethers.getContractFactory("ERC20Test");
    horseData = await ethers.getContractFactory("HorseData");
    core = await ethers.getContractFactory("Core");
    wethFundsReceiver = await ethers.getContractFactory("GOPFundsReceiverWETH");
  });

  beforeEach("contracts instantiation", async () => {
    erc20 = await erc20.deploy(ethers.utils.parseUnits("100"));
    await erc20.deployed();

    horseData = await horseData.deploy();
    await horseData.deployed();

    core = await core.deploy(horseData.address);
    await core.deployed();

    wethFundsReceiver = await wethFundsReceiver.deploy(
      fundsReceiver.address,
      core.address,
      erc20.address
    );
    await wethFundsReceiver.deployed();

    wethFundsReceiver.grantRole(
      ethers.utils.formatBytes32String("gfr_owners"),
      blockchainBrain.address
    );
  });

  beforeEach("setup erc20 tokens", async () => {
    await erc20.transfer(bob.address, amount);
    await erc20.transfer(emily.address, amount);
    await erc20.connect(bob).approve(wethFundsReceiver.address, maxWETH);
    await erc20.connect(sasha).approve(wethFundsReceiver.address, maxWETH);
    await erc20.connect(emily).approve(wethFundsReceiver.address, maxWETH);
    await erc20.approve(wethFundsReceiver.address, maxWETH);
  });

  beforeEach("setup initial state of instances", async () => {
    await wethFundsReceiver.setDomainSeparator(
      "ZED WETH Funds Receiver",
      "1",
      chainId
    );

    await core.grantRole(
      ethers.utils.formatBytes32String("core_contracts"),
      owner.address
    );

    // 0
    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Male"),
      ethers.utils.formatBytes32String("FirstName"),
      ethers.utils.formatBytes32String("Black")
    );

    // 1
    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Female"),
      ethers.utils.formatBytes32String("Truc"),
      ethers.utils.formatBytes32String("violet")
    );

    // 2
    await core.mintCustomHorse(
      bob.address,
      4,
      ethers.utils.formatBytes32String("Male"),
      ethers.utils.formatBytes32String("Trung"),
      ethers.utils.formatBytes32String("blue")
    );
  });

  describe("Initial data validation", async () => {
    it("should make owner admin of contract after deployment", async () => {
      expect(
        await wethFundsReceiver.hasRole(
          ethers.utils.formatBytes32String("gfr_owners_admin"),
          owner.address
        )
      ).to.be.true;
    });

    it("should not make the owner the funds receiver", async () => {
      expect(await wethFundsReceiver.isFundsReceiver()).to.be.false;
    });

    it("should assign the funds receiver to the address in the constructor", async () => {
      expect(await wethFundsReceiver.connect(fundsReceiver).isFundsReceiver())
        .to.be.true;
    });
  });

  describe("getters and setters", async () => {
    it("should change funds receiver wallet", async () => {
      expect(await wethFundsReceiver.connect(fundsReceiver).isFundsReceiver())
        .to.be.true;

      await wethFundsReceiver
        .connect(blockchainBrain)
        .changeFundsReceiver(nextFundsReceiver.address);

      expect(
        await wethFundsReceiver.connect(nextFundsReceiver).isFundsReceiver()
      ).to.be.true;
    });
  });

  describe("pausable", async () => {
    it("can only be paused by accounts with correct role", async () => {
      await wethFundsReceiver.connect(blockchainBrain).pause();
      await wethFundsReceiver.connect(blockchainBrain).unpause();

      expect(wethFundsReceiver.connect(bob).pause()).to.be.revertedWith(
        "GOP: Unauthorized"
      );
    });

    it("should revert if contract is paused", async () => {
      await wethFundsReceiver.connect(blockchainBrain).pause();

      expect(
        wethFundsReceiver
          .connect(bob)
          .receiveGOPFunds(
            ethers.utils.formatBytes32String("horse_code"),
            amount,
            ethers.utils.formatBytes32String("Bojack Horseman")
          )
      ).to.be.revertedWith("Pausable: paused");
    });

    it("should be able to receive funds after unpaused", async () => {
      await wethFundsReceiver.connect(blockchainBrain).pause();
      await wethFundsReceiver.connect(blockchainBrain).unpause();
      await wethFundsReceiver
        .connect(bob)
        .receiveGOPFunds(
          ethers.utils.formatBytes32String("horse_code"),
          amount,
          ethers.utils.formatBytes32String("Bojack Horseman")
        );

      expect(await erc20.balanceOf(fundsReceiver.address)).to.equal(amount);
      expect(await erc20.balanceOf(bob.address)).to.equal(0);
    });
  });

  describe("funds receiving", async () => {
    it("should fail for insufficient funds", async () => {
      expect(await erc20.balanceOf(sasha.address)).to.equal(0);

      expect(
        wethFundsReceiver
          .connect(sasha)
          .receiveGOPFunds(
            ethers.utils.formatBytes32String("horse_code"),
            amount,
            ethers.utils.formatBytes32String("Bojack Horseman")
          )
      ).to.be.revertedWith("ERC20: transfer amount exceeds balance");
    });

    it("should fail for unnamed _horseName", async () => {
      expect(
        wethFundsReceiver.receiveGOPFunds(
          ethers.utils.formatBytes32String(""),
          amount,
          ethers.utils.formatBytes32String("Bojack Horseman")
        )
      ).to.be.revertedWith("GOP: Code cannot be an empty string");
    });

    it("should fail for 0 _horsePrice", async () => {
      expect(
        wethFundsReceiver.receiveGOPFunds(
          ethers.utils.formatBytes32String("horse_code"),
          0,
          ethers.utils.formatBytes32String("Bojack Horseman")
        )
      ).to.be.revertedWith("GOP: Price cannot be 0");
    });

    it("should transfer funds to fundsReceiver", async () => {
      await wethFundsReceiver
        .connect(bob)
        .receiveGOPFunds(
          ethers.utils.formatBytes32String("horse_code"),
          amount,
          ethers.utils.formatBytes32String("Bojack Horseman")
        );

      expect(await erc20.balanceOf(fundsReceiver.address)).to.equal(amount);
      expect(await erc20.balanceOf(bob.address)).to.equal(0);
    });

    it("should fail for already used _horseName", async () => {
      expect(
        wethFundsReceiver.receiveGOPFunds(
          ethers.utils.formatBytes32String("horse_code"),
          amount,
          ethers.utils.formatBytes32String("Trung")
        )
      ).to.be.revertedWith("Core: name already taken");
    });
  });

  describe("isCodeUsed mapping", async () => {
    it("should fail after receiving funds more than once for the same horse", async () => {
      await wethFundsReceiver
        .connect(bob)
        .receiveGOPFunds(
          ethers.utils.formatBytes32String("horse_code"),
          amount,
          ethers.utils.formatBytes32String("Bojack Horseman")
        );

      expect(
        wethFundsReceiver
          .connect(emily)
          .receiveGOPFunds(
            ethers.utils.formatBytes32String("horse_code"),
            amount,
            ethers.utils.formatBytes32String("Bojack Horseman")
          )
      ).to.be.revertedWith("GOP: Code has already been used");

      expect(await erc20.balanceOf(fundsReceiver.address)).to.equal(amount);
      expect(await erc20.balanceOf(bob.address)).to.equal(0);
    });

    it("should not be able to receive funds for code mark as used", async () => {
      await wethFundsReceiver
        .connect(blockchainBrain)
        .markCodeAsUsed(ethers.utils.formatBytes32String("horse_code"));

      expect(
        wethFundsReceiver.receiveGOPFunds(
          ethers.utils.formatBytes32String("horse_code"),
          amount,
          ethers.utils.formatBytes32String("Bojack Horseman")
        )
      ).to.be.revertedWith("GOP: Code has already been used");
    });

    it("should be able to receive funds for code mark as unused", async () => {
      await wethFundsReceiver
        .connect(blockchainBrain)
        .markCodeAsUsed(ethers.utils.formatBytes32String("horse_code"));

      await wethFundsReceiver
        .connect(blockchainBrain)
        .markCodeAsUnused(ethers.utils.formatBytes32String("horse_code"));

      await wethFundsReceiver
        .connect(bob)
        .receiveGOPFunds(
          ethers.utils.formatBytes32String("horse_code"),
          amount,
          ethers.utils.formatBytes32String("Bojack Horseman")
        );
    });
  });

  describe("meta-transactions", async () => {
    // after("revert web3 provider", async () => {
    //   web3.setProvider(
    //     new Web3.providers.HttpProvider("http://127.0.0.1:8545")
    //   );
    // });

    // before("set web3 provider", async () => {
    //   web3.setProvider(ganache.provider({ accounts: metaTxsAccounts }));
    // });

    it("sends funds to fundReceiver with meta-txs", async () => {
      const initialOwnerBalance = await erc20.balanceOf(owner.address);
      let functionSignature = wethFundsReceiver.interface.encodeFunctionData(
        "receiveGOPFunds",
        [
          ethers.utils.formatBytes32String("horse_code"),
          amount,
          ethers.utils.formatBytes32String("Bojack Horseman"),
        ]
      );

      let typedData = await getTypedData({
        name: "ZED WETH Funds Receiver",
        version: "1",
        chainId: chainId,
        verifyingContract: wethFundsReceiver.address,
        nonce: await wethFundsReceiver.getNonce(owner.address),
        from: owner.address,
        functionSignature: functionSignature,
      });

      let signedData = await owner._signTypedData(
        typedData.domain,
        typedData.types,
        typedData.message
      );

      let { r, s, v } = await getRsvFromSig(signedData);

      await wethFundsReceiver.executeMetaTransaction(
        owner.address,
        functionSignature,
        r,
        s,
        v
      );

      expect(await erc20.balanceOf(fundsReceiver.address)).to.equal(amount);
    });
  });
});

