const Breeding = artifacts.require("Breeding");
const BreedingLimit = artifacts.require("BreedingLimit");
const BreedingProxy = artifacts.require("BreedingProxy");
const ProxyAdmin = artifacts.require("ZedProxyAdmin");
const BreedTypes = artifacts.require("BreedTypes");
const Core = artifacts.require("Core");
const ERC20Test = artifacts.require("ERC20Test");
const HorseData = artifacts.require("HorseData");
const StudService = artifacts.require("StudServiceV5");
const ganache = require("ganache-core");

import {
  funcFromABI,
  signTxData,
  getTypedData,
  getRsvFromSig,
  metaTxsAccounts,
  increaseTimeBySeconds,
  mineBlock,
} from "../../../utils";

contract("BreedingLimit", (acc) => {
  const DEFAULT_COLOR = web3.utils.toHex("Midnight Black"),
    DEFAULT_AMOUNT = web3.utils.toWei("0.2", "ether"),
    MAX_WETH = web3.utils.toWei("1000", "ether");

  let breedingLimit,
    breedingLimitLogic,
    breedingLimitProxy,
    breeding,
    breedingLogic,
    breedingProxy,
    proxyAdmin,
    core,
    breedTypes,
    studService,
    horseData,
    erc20,
    chainId;

  let owner = acc[0],
    poolAddress = acc[1];

  beforeEach("deploy instances", async () => {
    horseData = await HorseData.new();
    core = await Core.new(horseData.address);
    chainId = await web3.eth.net.getId();

    breedTypes = await BreedTypes.new();

    studService = await StudService.new(
      core.address,
      horseData.address,
      breedTypes.address
    );

    erc20 = await ERC20Test.new(web3.utils.toWei("100", "ether"));

    // Deploys Breeding Logic instance
    breedingLogic = await Breeding.new();

    // Deploys Breeding Admin to be used as admin of BreedingProxy and BreedingLimitProxy
    proxyAdmin = await ProxyAdmin.new();

    // Deploys proxy instance
    breedingProxy = await BreedingProxy.new(
      breedingLogic.address,
      proxyAdmin.address
    );

    breeding = await Breeding.at(breedingProxy.address);

    ///////

    // Relayer
    breedingLimitLogic = await BreedingLimit.new();

    // Use the same BreedingProxy and ProxyAdmin as it can manage multiple proxies
    breedingLimitProxy = await BreedingProxy.new(
      breedingLimitLogic.address,
      proxyAdmin.address
    );

    breedingLimit = await BreedingLimit.at(breedingLimitProxy.address);
  });

  beforeEach("setup initial state of instances", async () => {
    await breedTypes.grantRole(web3.utils.toHex("breed_types_owners"), owner);

    await breedingLimit.initialize(core.address, breeding.address);
    await breedingLimit.grantRole(
      web3.utils.toHex("breeding_limit_owners"),
      owner
    );

    await breedingLimit.setDomainSeparator("ZED BreedingLimit", "1", chainId);

    await breeding.initialize(
      core.address,
      studService.address,
      breedTypes.address,
      erc20.address,
      poolAddress,
      acc[4]
    );

    await breeding.grantRole(
      web3.utils.toHex("breeding_owners"),
      breedingLimit.address
    );

    await breedTypes.grantRole(
      web3.utils.toHex("breed_types_contracts"),
      breeding.address
    );

    await core.grantRole(web3.utils.toHex("core_contracts"), owner);

    // Core need to grant CORE_CONTRACTS_ROLE and CORE_OWNERS_ROLE for Breeding to able to invoke method Core.mintOffspring
    await core.grantRole(web3.utils.toHex("core_contracts"), breeding.address);
    await core.grantRole(web3.utils.toHex("core_owners"), breeding.address);

    // We cannot interact with ID 0
    await core.mintCustomHorse(
      owner,
      4,
      web3.utils.toHex("Male"),
      web3.utils.toHex("FirstName"),
      web3.utils.toHex("Black")
    );

    // ID 1
    await core.mintCustomHorse(
      owner,
      4,
      web3.utils.toHex("Female"),
      web3.utils.toHex("Truc"),
      web3.utils.toHex("violet")
    );

    // ID 2
    await core.mintCustomHorse(
      owner,
      4,
      web3.utils.toHex("Male"),
      web3.utils.toHex("Trung"),
      web3.utils.toHex("blue")
    );

    await studService.grantRole(web3.utils.toHex("stud_owners"), owner);
    await studService.putInStud(2, DEFAULT_AMOUNT, 86400);

    await erc20.transfer(acc[2], DEFAULT_AMOUNT);
    await erc20.transfer(acc[5], DEFAULT_AMOUNT);
    await erc20.approve(breeding.address, MAX_WETH, {
      from: acc[2],
    });
    await erc20.approve(breeding.address, MAX_WETH, {
      from: acc[5],
    });
    await erc20.approve(breeding.address, MAX_WETH);
  });

  describe("relayMix", async () => {
    it("should record breed data if it is not saved", async () => {
      await doNormalMix();

      let maleRecord = await breedingLimit.getBreedingRecord(2);

      assert.isTrue(maleRecord[0]);
      // Male limit is 3
      assert.equal(maleRecord[1].toNumber(), 2);

      let femaleRecord = await breedingLimit.getBreedingRecord(1);

      assert.isTrue(femaleRecord[0]);
      // Female limit is 1
      assert.equal(femaleRecord[1].toNumber(), 0);
    });

    it("should use correct reset date if horse hasn't been used in months", async () => {
      let resetDays = await breedingLimit.breedingCycle();
      let expectedDifference = resetDays.toNumber() * 5;

      await increaseTimeBySeconds(resetDays.toNumber() * 4);
      await mineBlock();
      await doNormalMix();

      let maleRecord = await breedingLimit.getBreedingRecord(2);
      // Horse next breeding reset should be in ~4-5 months (`resetDays` * 4)
      let horseData = await core.getHorseData(2);
      let datesDifference = maleRecord[2].toNumber() - horseData[2].toNumber();

      assert.equal(datesDifference, expectedDifference);

      let femaleRecord = await breedingLimit.getBreedingRecord(1);
      // Horse next breeding reset should be in ~4-5 months (`resetDays` * 4)
      horseData = await core.getHorseData(1);
      datesDifference = femaleRecord[2].toNumber() - horseData[2].toNumber();

      assert.equal(datesDifference, expectedDifference);
    });

    it("should update breeding counter and reset of horses", async () => {
      let resetDays = await breedingLimit.breedingCycle();
      await doNormalMix();
      await increaseTimeBySeconds(resetDays.toNumber() * 2);
      await mineBlock();

      // If the reset wasn't working, this transaction wouldn't go through as one of the horses shouldn't have breeds left
      await doNormalMix();

      let maleRecord = await breedingLimit.getBreedingRecord(2);
      assert.equal(maleRecord[1].toNumber(), 2);

      let femaleRecord = await breedingLimit.getBreedingRecord(1);
      assert.equal(femaleRecord[1], 0);
    });

    describe("reverts", async () => {
      it("should revert not the owner of the female sends the tx", async () => {
        try {
          await doNormalMix({ from: acc[4] });
          assert.fail("Expected revert not received");
        } catch (e) {
          assert.equal(
            e.reason,
            "BreedingLimit: female owner should be the initiator"
          );
        }
      });

      it("should revert if female horse has reached limit", async () => {
        await doNormalMix();

        try {
          await doNormalMix();
          assert.fail("Expected revert not received");
        } catch (e) {
          assert.equal(
            e.reason,
            "BreedingLimit: female breeding limit reached"
          );
        }
      });

      it("should revert if horse has 0 breeds left and shouldn't be updated yet", async () => {
        await doNormalMix();

        try {
          await doNormalMix();
          assert.fail("Expected revert not received");
        } catch (e) {
          assert.equal(
            e.reason,
            "BreedingLimit: female breeding limit reached"
          );
        }
      });
    });
  });

  describe("recordHorse", async () => {
    it("should record data for a horse", async () => {
      let tx = await breedingLimit.recordHorse(2);
      let record = await breedingLimit.getBreedingRecord(2);

      assert.isTrue(record[0]);
      assert.equal(record[1].toNumber(), 3);
      assert.equal(tx.receipt.logs[0].event, "HorseRecordInserted");

      tx = await breedingLimit.recordHorse(1);
      record = await breedingLimit.getBreedingRecord(1);

      assert.isTrue(record[0]);
      assert.equal(record[1].toNumber(), 1);
      assert.equal(tx.receipt.logs[0].event, "HorseRecordInserted");
    });

    describe("reverts", async () => {
      it("should revert if horse is already recorded", async () => {
        await doNormalMix();

        try {
          await breedingLimit.recordHorse(2);
          assert.fail("Expected revert not received");
        } catch (e) {
          assert.equal(e.reason, "BreedingLimit: horse already recorded");
        }
      });
    });
  });

  describe("setBreedingCycle", async () => {
    it("should update the breeding cycle", async () => {
      let tx = await breedingLimit.setBreedingCycle(1);
      let newCycle = await breedingLimit.breedingCycle();

      assert.equal(newCycle.toNumber(), 1);
      assert.equal(tx.receipt.logs[0].event, "BreedingCycleUpdated");
    });
  });

  describe("updateBreedingLimits", async () => {
    it("should update the breeding limits", async () => {
      let tx = await breedingLimit.updateBreedingLimits(1, 3);
      let maleLimit = await breedingLimit.maleLimit();
      let femaleLimit = await breedingLimit.femaleLimit();

      assert.equal(maleLimit.toNumber(), 1);
      assert.equal(femaleLimit.toNumber(), 3);
      assert.equal(tx.receipt.logs[0].event, "HorsesLimitsUpdated");
    });
  });

  describe("updateHorseRecord", async () => {
    it("should update horse record with given args", async () => {
      let tx = await breedingLimit.updateHorseRecord(1, 10, 123123);
      let record = await breedingLimit.getBreedingRecord(1);

      assert.isTrue(record[0]);
      assert.equal(record[1].toNumber(), 10);
      assert.equal(record[2].toNumber(), 123123);
    });
  });

  describe("meta-transactions", async () => {
    beforeEach("set web3 provider", async () => {
      web3.setProvider(ganache.provider({ accounts: metaTxsAccounts }));
    });

    it("should breed and save horse's record", async () => {
      let functionSignature = web3.eth.abi.encodeFunctionCall(
        await funcFromABI(breedingLimit.abi, "relayMix"),
        [2, 1, DEFAULT_COLOR, DEFAULT_AMOUNT]
      );

      let typedData = await getTypedData({
        name: "ZED BreedingLimit",
        version: "1",
        chainId: chainId,
        verifyingContract: breedingLimit.address,
        nonce: await breedingLimit.getNonce(owner),
        from: owner,
        functionSignature: functionSignature,
      });

      let signedData = await signTxData(owner, typedData);

      let { r, s, v } = await getRsvFromSig(signedData);

      await breedingLimit.executeMetaTransaction(
        owner,
        functionSignature,
        r,
        s,
        v
      );

      let maleRecord = await breedingLimit.getBreedingRecord(2);

      assert.isTrue(maleRecord[0]);
      // Male limit is 3
      assert.equal(maleRecord[1].toNumber(), 2);

      let femaleRecord = await breedingLimit.getBreedingRecord(1);

      assert.isTrue(femaleRecord[0]);
      // Female limit is 1
      assert.equal(femaleRecord[1].toNumber(), 0);
    });
  });

  const doNormalMix = async (opts = { from: owner }) => {
    await breedingLimit.relayMix(2, 1, DEFAULT_COLOR, DEFAULT_AMOUNT, opts);
  };
});

