const ganache = require("ganache-core");

import {
  funcFromABI,
  signTxData,
  getTypedData,
  getRsvFromSig,
  metaTxsAccounts,
  increaseTimeBySeconds,
  mineBlock,
  chainId,
} from "../../../utils";
import { expect } from "chai";

const DEFAULT_COLOR = ethers.utils.formatBytes32String("Midnight Black"),
  DEFAULT_AMOUNT = ethers.utils.parseUnits("0.2"),
  MAX_WETH = ethers.utils.parseUnits("1000");

const DEFAULT_MIX_PARAMS = [2, 1, DEFAULT_COLOR, DEFAULT_AMOUNT];

describe("BreedingLimit", () => {
  let breedingLimit,
    breedingLimitLogic,
    breedingLimitProxy,
    breeding,
    breedingLogic,
    breedingProxy,
    breedingProxyInstance,
    proxyAdmin,
    core,
    breedTypes,
    studService,
    horseData,
    erc20;

  let acc, owner, poolAddress;

  before("populate accs", async () => {
    acc = await ethers.getSigners();
    owner = acc[0];
    poolAddress = acc[1];
  });

  beforeEach("get factories", async () => {
    horseData = await ethers.getContractFactory("HorseData");
    core = await ethers.getContractFactory("Core");
    breedTypes = await ethers.getContractFactory("BreedTypes");
    studService = await ethers.getContractFactory("StudServiceV5");
    erc20 = await ethers.getContractFactory("ERC20Test");
    breedingLogic = await ethers.getContractFactory("Breeding");
    proxyAdmin = await ethers.getContractFactory("ZedProxyAdmin");
    breedingProxyInstance = await ethers.getContractFactory("BreedingProxy");
    breedingLimitLogic = await ethers.getContractFactory("BreedingLimit");
  });

  beforeEach("deploy instances", async () => {
    horseData = await horseData.deploy();
    await horseData.deployed();

    core = await core.deploy(horseData.address);
    await core.deployed();

    breedTypes = await breedTypes.deploy();
    await breedTypes.deployed();

    studService = await studService.deploy(
      core.address,
      horseData.address,
      breedTypes.address
    );
    await studService.deployed();

    erc20 = await erc20.deploy(ethers.utils.parseUnits("100"));
    await erc20.deployed();

    // Deploys Breeding Logic instance
    breedingLogic = await breedingLogic.deploy();
    await breedingLogic.deployed();

    // Deploys Breeding Admin to be used as admin of BreedingProxy and BreedingLimitProxy
    proxyAdmin = await proxyAdmin.deploy();
    await proxyAdmin.deployed();

    // Deploys proxy instance
    breedingProxy = await breedingProxyInstance.deploy(
      breedingLogic.address,
      proxyAdmin.address
    );
    await breedingProxy.deployed();

    breeding = new ethers.Contract(
      breedingProxy.address,
      breedingLogic.interface
    );
    breeding = breeding.connect(owner);

    ///////

    // Relayer
    breedingLimitLogic = await breedingLimitLogic.deploy();
    await breedingLimitLogic.deployed();

    // Use the same BreedingProxy and ProxyAdmin as it can manage multiple proxies
    breedingLimitProxy = await breedingProxyInstance.deploy(
      breedingLimitLogic.address,
      proxyAdmin.address
    );
    await breedingLimitProxy.deployed();

    breedingLimit = new ethers.Contract(
      breedingLimitProxy.address,
      breedingLimitLogic.interface
    );
    breedingLimit = breedingLimit.connect(owner);
  });

  beforeEach("setup initial state of instances", async () => {
    await breedTypes.grantRole(
      ethers.utils.formatBytes32String("breed_types_owners"),
      owner.address
    );

    await breedingLimit.initialize(core.address, breeding.address);
    await breedingLimit.grantRole(
      ethers.utils.formatBytes32String("breeding_limit_owners"),
      owner.address
    );

    await breedingLimit.setDomainSeparator("ZED BreedingLimit", "1", chainId);

    await breeding.initialize(
      core.address,
      studService.address,
      breedTypes.address,
      erc20.address,
      poolAddress.address,
      acc[4].address
    );

    await breeding.grantRole(
      ethers.utils.formatBytes32String("breeding_owners"),
      breedingLimit.address
    );

    await breedTypes.grantRole(
      ethers.utils.formatBytes32String("breed_types_contracts"),
      breeding.address
    );

    await core.grantRole(
      ethers.utils.formatBytes32String("core_contracts"),
      owner.address
    );

    // Core need to grant CORE_CONTRACTS_ROLE and CORE_OWNERS_ROLE for Breeding to able to invoke method Core.mintOffspring
    await core.grantRole(
      ethers.utils.formatBytes32String("core_contracts"),
      breeding.address
    );
    await core.grantRole(
      ethers.utils.formatBytes32String("core_owners"),
      breeding.address
    );

    // We cannot interact with ID 0
    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Male"),
      ethers.utils.formatBytes32String("FirstName"),
      ethers.utils.formatBytes32String("Black")
    );

    // ID 1
    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Female"),
      ethers.utils.formatBytes32String("Truc"),
      ethers.utils.formatBytes32String("violet")
    );

    // ID 2
    await core.mintCustomHorse(
      owner.address,
      4,
      ethers.utils.formatBytes32String("Male"),
      ethers.utils.formatBytes32String("Trung"),
      ethers.utils.formatBytes32String("blue")
    );

    await studService.grantRole(
      ethers.utils.formatBytes32String("stud_owners"),
      owner.address
    );
    await studService.putInStud(2, DEFAULT_AMOUNT, 86400);

    await erc20.transfer(acc[2].address, DEFAULT_AMOUNT);
    await erc20.transfer(acc[5].address, DEFAULT_AMOUNT);
    await erc20.connect(acc[2]).approve(breeding.address, MAX_WETH);
    await erc20.connect(acc[5]).approve(breeding.address, MAX_WETH);
    await erc20.approve(breeding.address, MAX_WETH);
  });

  describe("meta-transactions", async () => {
    // `relayMix` is being tested here becuse the contract forbids calling it directly
    // and instead it should be executed through meta transactions.
    describe("relayMix", async () => {
      it("should record breed data if it is not saved", async () => {
        await buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS);

        let maleRecord = await breedingLimit.getBreedingRecord(2);

        expect(maleRecord[0]).to.be.true;
        // Male limit is 3
        expect(maleRecord[1]).to.equal(2);

        let femaleRecord = await breedingLimit.getBreedingRecord(1);

        expect(femaleRecord[0]).to.be.true;
        // Female limit is 1
        expect(femaleRecord[1]).to.equal(0);
      });

      it("should use correct reset date if horse hasn't been used in months", async () => {
        let resetDays = await breedingLimit.breedingCycle();
        let expectedDifference = resetDays.toNumber() * 5;

        await increaseTimeBySeconds(resetDays.toNumber() * 4);
        await mineBlock();
        await buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS);

        let maleRecord = await breedingLimit.getBreedingRecord(2);
        // Horse next breeding reset should be in ~4-5 months (`resetDays` * 4)
        let horseData = await core.getHorseData(2);
        let datesDifference =
          maleRecord[2].toNumber() - horseData[2].toNumber();

        expect(datesDifference).to.equal(expectedDifference);

        let femaleRecord = await breedingLimit.getBreedingRecord(1);
        // Horse next breeding reset should be in ~4-5 months (`resetDays` * 4)
        horseData = await core.getHorseData(1);
        datesDifference = femaleRecord[2].toNumber() - horseData[2].toNumber();

        expect(datesDifference).to.equal(expectedDifference);
      });

      it("should update breeding counter and reset of horses", async () => {
        let resetDays = await breedingLimit.breedingCycle();

        await buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS);
        await increaseTimeBySeconds(resetDays.toNumber() * 2);
        await mineBlock();

        // If the reset wasn't working, this transaction wouldn't go through as one of the horses shouldn't have breeds left
        await buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS);

        let maleRecord = await breedingLimit.getBreedingRecord(2);
        expect(maleRecord[1]).to.equal(2);

        let femaleRecord = await breedingLimit.getBreedingRecord(1);
        expect(femaleRecord[1]).to.equal(0);
      });

      describe("reverts", async () => {
        it("should revert not the owner of the female sends the tx", async () => {
          expect(
            buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS, acc[4])
          ).to.be.revertedWith(
            "BreedingLimit: female owner should be the initiator"
          );
        });

        it("should revert if female horse has reached limit", async () => {
          await buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS);

          expect(
            buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS)
          ).to.be.revertedWith("BreedingLimit: female breeding limit reached");
        });

        it("should revert if horse has 0 breeds left and shouldn't be updated yet", async () => {
          let resetDays = await breedingLimit.breedingCycle();

          await buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS);
          await increaseTimeBySeconds(resetDays.toNumber() - 100);
          await mineBlock();

          expect(
            buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS)
          ).to.be.revertedWith("BreedingLimit: female breeding limit reached");
        });
      });
    });
  });

  describe("recordHorse", async () => {
    it("should record data for a horse", async () => {
      let tx = await breedingLimit.recordHorse(2);
      let record = await breedingLimit.getBreedingRecord(2);

      expect(record[0]).to.be.true;
      expect(record[1]).to.equal(3);

      expect(tx).to.emit(breedingLimit, "HorseRecordInserted").withArgs(2);

      tx = await breedingLimit.recordHorse(1);
      record = await breedingLimit.getBreedingRecord(1);

      expect(record[0]).to.be.true;
      expect(record[1]).to.equal(1);

      expect(tx).to.emit(breedingLimit, "HorseRecordInserted").withArgs(1);
    });

    describe("reverts", async () => {
      it("should revert if horse is already recorded", async () => {
        await buildAndExecuteMetaTx("relayMix", DEFAULT_MIX_PARAMS);

        expect(breedingLimit.recordHorse(2)).to.be.revertedWith(
          "BreedingLimit: horse already recorded"
        );
      });
    });
  });

  describe("setBreedingCycle", async () => {
    it("should update the breeding cycle", async () => {
      let tx = await breedingLimit.setBreedingCycle(1);
      let newCycle = await breedingLimit.breedingCycle();

      expect(newCycle).to.equal(1);
      expect(tx)
        .to.emit(breedingLimit, "BreedingCycleUpdated")
        .withArgs(owner.address, 1);
    });
  });

  describe("updateBreedingLimits", async () => {
    it("should update the breeding limits", async () => {
      let tx = await breedingLimit.updateBreedingLimits(1, 3);
      let maleLimit = await breedingLimit.maleLimit();
      let femaleLimit = await breedingLimit.femaleLimit();

      expect(maleLimit).to.equal(1);
      expect(femaleLimit).to.equal(3);

      expect(tx)
        .to.emit(breedingLimit, "HorsesLimitsUpdated")
        .withArgs(owner.address, 1, 3);
    });
  });

  describe("updateHorseRecord", async () => {
    it("should update horse record with given args", async () => {
      let tx = await breedingLimit.updateHorseRecord(1, 10, 123123);
      let record = await breedingLimit.getBreedingRecord(1);

      expect(record[0]).to.be.true;
      expect(record[1]).to.equal(10);
      expect(record[2]).to.equal(123123);
    });
  });

  const buildAndExecuteMetaTx = async (method, funcParams, account = owner) => {
    let functionSignature = breedingLimit.interface.encodeFunctionData(
      method,
      funcParams
    );

    let typedData = await getTypedData({
      name: "ZED BreedingLimit",
      version: "1",
      chainId: chainId,
      verifyingContract: breedingLimit.address,
      nonce: await breedingLimit.getNonce(account.address),
      from: account.address,
      functionSignature: functionSignature,
    });

    let signedData = await account._signTypedData(
      typedData.domain,
      typedData.types,
      typedData.message
    );

    let { r, s, v } = await getRsvFromSig(signedData);

    await breedingLimit.executeMetaTransaction(
      account.address,
      functionSignature,
      r,
      s,
      v
    );
  };
});

